INSERT INTO `code_code` VALUES (6,3,1,'代码片段','#encoding:utf-8\r\nfrom django import forms\r\nfrom accounts.models import UserProfile\r\n\r\nclass ProfileForm(forms.ModelForm):\r\n    class Meta:\r\n        model = UserProfile\r\n        widgets = {\'introduction\':forms.Textarea()}\r\n        exclude = (\'score\',\'deleted\',\'photo\',\'user\',\'city\')\r\n\r\nclass PhotoForm(forms.Form):\r\n    photo = forms.ImageField(label=\'\',max_length=1024)'),(4,3,2,'代码片段','UPDATE django_comments\r\nSET content_type_id = 14\r\nWHERE content_type_id = 34'),(7,7,19,'run.py','#encoding: utf-8\r\n#author:t-y  \r\n#http://pythoner.net\r\n\r\nimport time,math,os,re,urllib,urllib2,cookielib\r\n\r\nclass douban_robot:\r\n    email = \'你的登陆账号\'\r\n    password = \'你的登陆密码\'\r\n    login_path = \'https://www.douban.com/accounts/login\'\r\n    \r\n    def __init__(self):\r\n        self.cj =cookielib.LWPCookieJar()\r\n        self.opener = urllib2.build_opener(urllib2.HTTPCookieProcessor(self.cj))\r\n        urllib2.install_opener(self.opener)\r\n        self.opener.addheaders = [(\'User-agent\',\'Opera/9.23\')]\r\n\r\n    #模拟登陆\r\n    def login(self):\r\n        post_data = urllib.urlencode({\r\n            \'form_email\':self.email,\r\n            \'form_password\':self.password,\r\n            \'remeber\':\'on\',\r\n            })\r\n        #发起HTTP请求\r\n        request = urllib2.Request(self.login_path,post_data)\r\n        html = self.opener.open(request).read()\r\n        print re.findall(\'<h1>(.*)</h1>\',html,re.S)[0].decode(\'utf8\') \r\n        get_url = self.opener.open(request).geturl()\r\n        if get_url == \'http://www.douban.com/\':\r\n            self.cj.save(\'douban.cookie\')\r\n            print \'Login success !\'\r\n            return True\r\n        else:\r\n            print get_url\r\n            print \'Login error\'\r\n            return False\r\n\r\n    def run(self,d_url):\r\n        self.login()\r\n        return \r\n        self.post_url = d_url       \r\n        while True:\r\n            if self.sleep_time() > 10:\r\n                timer = self.sleep_time()-10\r\n                print \'%s:waiting %s\' %(time.strftime(\'%H:%M:%S\'),timer) \r\n                time.sleep(timer)\r\n            #准备内容\r\n            response = urllib2.Request(self.post_url)\r\n            html = urllib2.urlopen(response).read()\r\n            #获取表单令牌\r\n            ck = re.findall(\'(?<=name=\\\"ck\\\" value=\\\").*(?=\\\"\\/><\\/div>)\',html,re.S)[0]\r\n            if ck[0]<>\'\':\r\n                print \'get ck success!\'\r\n            post_data = urllib.urlencode({\r\n                \'ck\':ck,\r\n                \'rev_title\':\'(⊙o⊙)…\',\r\n                \'rev_text\':\'主人不在，Wall-e出来冒泡泡 ~\\(≧▽≦)/~\',\r\n                \'rev_submit\':\'好了，发言\',\r\n                })       \r\n            print \'content has ready\'\r\n            \r\n            timer = self.sleep_time()\r\n            time.sleep(timer)\r\n            dt = list(time.localtime())\r\n            minit = dt[4]\r\n            second = dt[5]\r\n            if minit==second==0:\r\n                #提交内容\r\n                request = urllib2.Request(self.post_url,post_data)\r\n                urllib2.urlopen(request)\r\n\r\n    def sleep_time(self):\r\n        dt = list(time.localtime())\r\n        hour = dt[3]\r\n        minut = dt[4]\r\n        second = dt[5]\r\n        return 3600-(minut*60+second)\r\n\r\n    def get_title(self):\r\n        title = re.findall(\'<title>(.*)</title>\',self.html,re.S)[0].decode(\'utf8\')   \r\n        h1 = re.findall(\'<h1>(.*)</h1>\',self.html,re.S)[0].decode(\'utf8\')\r\n        print \'%s--%s\' %(title,h1)\r\n\r\n    def write_log(self,text):\r\n        os.system(\'cls\')\r\n        print text\r\n        log = open(\'log.txt\',\'a\')\r\n        log.write(text)\r\n        log.close()      \r\n\r\nurl = \'http://www.douban.com/group/python/new_topic\' # 小组发言页面地址\r\napp = douban_robot()\r\napp.run(url)\r\n'),(8,8,19,'代码片段','#encoding:utf-8\r\n\"\"\"\r\nfile: 验证码模块\r\ndate: 2011-4-26\r\nauthor:pythoner.net\r\n\"\"\"\r\n\r\nfrom django.http import HttpResponse\r\nimport Image,ImageDraw,ImageFont,random,StringIO\r\n\r\n\r\n\r\ndef display(request):\r\n    \"\"\"\r\n    background  #随机背景颜色\r\n    line_color #随机干扰线颜色\r\n    img_width = #画布宽度\r\n    img_height = #画布高度\r\n    font_color = #验证码字体颜色\r\n    font_size = #验证码字体尺寸\r\n    font = I#验证码字体\r\n    \"\"\"\r\n    \r\n    string = {\'number\':\'12345679\',\r\n              \'litter\':\'ACEFGHKMNPRTUVWXY\'}\r\n    background = (random.randrange(230,255),random.randrange(230,255),random.randrange(230,255))\r\n    line_color = (random.randrange(0,255),random.randrange(0,255),random.randrange(0,255))\r\n    img_width = 58\r\n    img_height = 22\r\n    font_color = [\'black\',\'darkblue\',\'darkred\']\r\n    font_size = 14\r\n    font = ImageFont.truetype(\'ARIAL.TTF\',font_size)\r\n    request.session[\'verify\'] = \'\'\r\n\r\n    #新建画布\r\n    im = Image.new(\'RGB\',(img_width,img_height),background)\r\n    draw = ImageDraw.Draw(im)\r\n    code = random.sample(string[\'litter\'],4)\r\n    #新建画笔\r\n    draw = ImageDraw.Draw(im)\r\n\r\n    #画干扰线\r\n    for i in range(random.randrange(3,5)):\r\n        xy = (random.randrange(0,img_width),random.randrange(0,img_height),\r\n              random.randrange(0,img_width),random.randrange(0,img_height))\r\n        draw.line(xy,fill=line_color,width=1)\r\n    \r\n    #写入验证码文字\r\n    x = 2\r\n    for i in code: \r\n        y = random.randrange(0,10)\r\n        draw.text((x,y), i, font=font, fill=random.choice(font_color))\r\n        x += 14\r\n        request.session[\'verify\'] += i\r\n    del x\r\n    \r\n    del draw\r\n    buf = StringIO.StringIO()\r\n    im.save(buf,\'gif\')\r\n    buf.closed\r\n    return HttpResponse(buf.getvalue(),\'image/gif\')\r\n'),(9,9,1,'代码片段','#!/usr/bin/python\r\n# coding=utf-8\r\nimport re\r\nfrom BeautifulSoup import BeautifulSoup as BS, NavigableString, Tag\r\n\r\nclass HTMLSummary():\r\n    ignore_tags = (\'title\', \'style\', \'script\')\r\n    # 多个连续的空白字符将被替换成一个, 下面是re模式.\r\n    multi_spaces_pat = re.compile(r\'\\s{2,}\')\r\n\r\n    def __init__(self, html, char_limit=300):\r\n        self.char_limit = char_limit\r\n        self.char_cnt = 0\r\n        self._doc = BS(html)\r\n        self._exec(self._doc)\r\n\r\n    def __str__(self):\r\n        return str(self._doc)\r\n\r\n    def _exec(self, doc):\r\n        for i in doc:\r\n            if type(i) == NavigableString:\r\n                pretty_str = HTMLSummary.multi_spaces_pat.sub(\' \', i)\r\n                if i.strip() and self.char_cnt + len(pretty_str) >= self.char_limit:\r\n                    self._cut_after(i)\r\n                    to_pick = pretty_str[0: self.char_limit - self.char_cnt]\r\n                    i.replaceWith(to_pick)\r\n                    # 以实际有效(被浏览器显示)的字符数为准, 该方法并不精确:\r\n                    # 比如: <span> inline text</span>, 前面的空格将被计算在内,\r\n                    # 而它是否显示, 则取决于它是否是一个块元素的第一个子元素;\r\n                    # 另外, <p> para</p> 中的前导空格并未排除,\r\n                    # 因为无法预知它是否被CSS定义成行内元素.\r\n                    self.char_cnt += len(to_pick)\r\n                    break;\r\n                elif i.strip(): # 忽略只有空白字符的文本元素\r\n                    self.char_cnt += len( HTMLSummary.multi_spaces_pat.sub(\' \', i) )\r\n            elif type(i) == Tag:\r\n                if i.name not in HTMLSummary.ignore_tags:\r\n                    self._exec(i)\r\n\r\n    def _cut_after(self, node):\r\n        \'\'\'删除指定节点之后所有的同级节点,\r\n        然后删除该节点父节点的所有同级节点(即比自己父亲小的所有叔节点),\r\n        以此递归...\r\n        \'\'\'\r\n        cur = node.nextSibling\r\n        parent = node.parent      # 父节点\r\n        while cur:\r\n            nextpeer = cur.nextSibling\r\n            if type(cur) == Tag and cur.name in HTMLSummary.ignore_tags:\r\n                pass\r\n            else:\r\n                cur.extract()\r\n            cur = nextpeer\r\n        if parent:\r\n            self._cut_after(parent)\r\n\r\n    def prettify(self): return self._doc.prettify()\r\n\r\n\r\n############# TEST #################\r\nimport sys\r\ndef main():\r\n    html = file(sys.argv[1]).read();\r\n    su = HTMLSummary(html, int(sys.argv[2]))\r\n    print su#.prettify()\r\n\r\nif __name__ == \"__main__\":\r\n    main()\r\n'),(10,10,19,'代码片段','\'\'\'\r\nPython XSS payload encoder\r\nAuthor: BGS (rstcenter.com)\r\nContributor cmiN (rstcenter.com)\r\nDate: 13 August 2011\r\nVersion: Python 2.7\r\n\'\'\'\r\n#!/usr/bin/env python\r\n\r\nimport time\r\nimport sys\r\nimport urllib2 \r\nimport base64\r\n\r\n\r\n\r\n\r\ndef main():\r\n   \r\n    try:\r\n        if  sys.argv[1] == \"help\":\r\n            print \'[-]\'+time.ctime()\r\n            print\'\'\'[-]Instructions:\r\n                encoder.py <encoding type> \"<string>\"\r\n                Available encodings: ascii b64 hex url\r\n[-]Exiting... \r\n             \'\'\'\r\n        elif sys.argv[1] == \"b64\":\r\n            b64_encode()\r\n        elif sys.argv[1] == \"ascii\":\r\n            ascii_encode()\r\n        elif sys.argv[1] == \"hex\":\r\n            hex_encode()\r\n        elif sys.argv[1] == \"url\":\r\n            url_encode()\r\n            \r\n        else:\r\n            sys.exit(1)\r\n    except Exception, e:\r\n        print \'Type \"encoder.py help\" for instructions! \'\r\n        sys.exit(1)\r\n    \r\n    \r\ndef b64_encode():\r\n    payload = sys.argv[2]\r\n    encoded = base64.standard_b64encode(payload)\r\n    print \' ################## B64 String #######################\'\r\n    print \'\'\r\n    print \'String:\' + encoded\r\n    print \'\'\r\n    print \"#################### >>EOF<< #########################\"\r\n\r\n    \r\ndef ascii_encode():\r\n    payload = sys.argv[2]\r\n    string = \'\'\r\n    \r\n    for w in payload:\r\n        string += str(ord(w)) + \",\"\r\n    print \' ################## ASCII String #####################\'\r\n    print \'\'\r\n    print \'string.fromCharCode(\' + string.strip(\",\") +\')\' \r\n    print \'\'\r\n    print \"#################### >>EOF<< #########################\"\r\n\r\n\r\ndef hex_encode():\r\n    payload = sys.argv[2]\r\n    encoded = payload.encode(\'hex\')\r\n    print \' ################## HEX String #######################\'\r\n    print \'\'\r\n    print \'String:\' + encoded\r\n    print \'\'\r\n    print \"#################### >>EOF<< #########################\"\r\n    \r\n    \r\n    \r\n\r\ndef url_encode():\r\n    payload = sys.argv[2]\r\n    encoded = urllib2.quote(payload.encode(\"utf8\"))   \r\n    print \' ################## URL String #######################\'\r\n    print \'\'\r\n    print \'String:\' + encoded\r\n    print \'\'\r\n    print \"#################### >>EOF<< #########################\"\r\n'),(11,11,19,'sina.py','# -*- coding: utf-8 -*-\r\n# Data:11-6-29 下午9:59\r\n# Author: T-y(master@t-y.me)\r\n# File:sina\r\n# http://pythoner.net\r\n\r\nfrom django.http import HttpResponse,HttpResponseRedirect\r\nfrom weibopy.auth import OAuthHandler\r\nfrom weibopy.error import WeibopError\r\nfrom weibopy.api import API\r\nfrom settings import DOMAIN,ROOT_PATH\r\n\r\nCONSUMER_KEY = \'此处填写你的APP_KEY\'\r\nCONSUMER_SECRET = \'此处填写你的APP_SKEY\'\r\n\r\ndef index(request):\r\n    \"\"\"\r\n    跳转到验证页面\r\n    \"\"\"\r\n\r\n    auth = OAuthHandler(CONSUMER_KEY,CONSUMER_SECRET,callback=DOMAIN+\'/accounts/login/sina/callback/\')\r\n    auth_url = auth.get_authorization_url()\r\n    request.session[\'request_token\'] = auth.request_token # 保存request_token备用\r\n    return HttpResponseRedirect(auth_url)\r\n\r\ndef callback(request):\r\n    \"\"\"\r\n    用户授权后的回调\r\n    \"\"\"\r\n    oauth_token = request.GET.get(\'oauth_token\',False)\r\n    oauth_verifier = request.GET.get(\'oauth_verifier\',False)\r\n    if not oauth_token or not  oauth_verifier:\r\n        return HttpResponseRedirect(\'/accounts/login/sina/\')\r\n    try:\r\n        auth = OAuthHandler(CONSUMER_KEY,CONSUMER_SECRET)\r\n        auth.request_token = request.session[\'request_token\'] # 写入request_token 以便换取access_token\r\n        request.session[\'request_token\'] = \'\'\r\n        request.session[\'access_token\'] = auth.get_access_token(oauth_verifier) # 得到access_token\r\n    except Exception,e:\r\n        return HttpResponse(\'认证出错！\'+str(e))\r\n\r\n    # 完成后，即可调用API得到受保护的信息，完成用户的登陆、注册等等操作，此处略去\r\n    \r\n    \r\n\r\n\r\n\r\n    \r\n'),(12,12,19,'代码片段','def cut_str(str, length=10):\r\n    \"\"\"\r\n    截取字符串，使得字符串长度等于length，并在字符串后加上省略号\r\n    \"\"\"\r\n    is_encode = False\r\n    try:\r\n        str_encode = str.encode(\'gb18030\') #为了中文和英文的长度一致（中文按长度2计算）\r\n        is_encode = True\r\n    except:\r\n        pass\r\n    if is_encode:\r\n        l = length*2\r\n        if l < len(str_encode):\r\n            l = l - 3\r\n            str_encode = str_encode[:l]\r\n            try:\r\n                str = str_encode.decode(\'gb18030\') + \'...\'\r\n            except:\r\n                str_encode = str_encode[:-1]\r\n                try:\r\n                    str = str_encode.decode(\'gb18030\') + \'...\'\r\n                except:\r\n                    is_encode = False\r\n    if not is_encode:\r\n        if length < len(str):\r\n            length = length - 2\r\n            return str[:length] + \'...\'\r\n    return str\r\n'),(13,13,23,'代码片段','<!-- \r\nauthor:pythoner.net\r\ndatetime:2011-09-02 \r\n -->\r\n<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\r\n<head>\r\n<meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\" />\r\n<title>HTML+CSS仿google选项卡效果</title>\r\n<style>\r\nhtml{\r\n    overflow-y: scroll;\r\n    margin:0;\r\n    padding:0;}\r\nbody {\r\n	padding: 0;\r\n	margin:0px 0 0 0;\r\n    line-height:26px;\r\n	color:#333;\r\n    background:#F9F9F9;}\r\nbody,input,textarea{\r\n	font-family: Verdana,sans-serif,\'宋体\';\r\n	font-size:12px;}\r\ndiv.tabs{\r\n    border-bottom: 1px solid #6B90DA;\r\n    background-color: #EBEFF9;\r\n    padding:4px 10px 0 10px;\r\n    margin:5px 0 10px 0;\r\n    height:24px;\r\n}\r\ndiv.tabs span.tab{\r\n    padding:4px 8px;\r\n    height:24px;\r\n}\r\ndiv.tabs a:hover{\r\n    background:none;\r\n    color:#000;\r\n}\r\ndiv.tabs span.selected{\r\n    background:#fff;\r\n    border: 1px solid #6B90DA;\r\n    border-bottom:1px solid #fff;\r\n    color:#fff;\r\n    font-weight:900;}\r\n/* 选中效果 */\r\ndiv.tabs span.selected a{\r\n    color:#000;}\r\n</style>\r\n</head>\r\n<body>\r\n<div class=\"tabs\">\r\n	<span class=\"tab selected\"><a href=\"#\">选项1</a></span>\r\n	<span class=\"tab\"><a href=\"#\">选项2</a></span>\r\n	<span class=\"tab\"><a href=\"#\">选项3</a></span>\r\n	<span class=\"tab\"><a href=\"#\">选项4</a></span>\r\n	<span class=\"tab\"><a href=\"#\">选项5</a></span>\r\n\r\n</div>\r\n</body>\r\n</html>\r\n'),(14,14,19,'排序算法.py','#encoding=utf-8\r\nimport random\r\nfrom copy import copy\r\n \r\ndef directInsertSort(seq):\r\n	\"\"\" 直接插入排序 \"\"\"\r\n	size = len(seq)\r\n	for i in range(1,size):\r\n		tmp, j = seq[i], i\r\n		while j > 0 and tmp < seq[j-1]:\r\n			seq[j], j = seq[j-1], j-1\r\n		seq[j] = tmp\r\n	return seq\r\n \r\ndef directSelectSort(seq):\r\n	\"\"\" 直接选择排序 \"\"\"\r\n	size = len(seq)\r\n	for i in range(0,size - 1):\r\n		k = i;j = i+1\r\n		while j < size:\r\n			if seq[j] < seq[k]:\r\n				k = j\r\n			j += 1\r\n		seq[i],seq[k] = seq[k],seq[i]\r\n	return seq\r\n \r\ndef bubbleSort(seq):\r\n	\"\"\"冒泡排序\"\"\"\r\n	size = len(seq)\r\n	for i in range(1,size):\r\n		for j in range(0,size-i):\r\n			if seq[j+1] < seq[j]:\r\n				seq[j+1],seq[j] = seq[j],seq[j+1]\r\n	return seq\r\n \r\ndef _divide(seq, low, high):\r\n	\"\"\"快速排序划分函数\"\"\"\r\n	tmp = seq[low]\r\n	while low != high:\r\n		while low < high and seq[high] >= tmp: high -= 1\r\n		if low < high:\r\n			seq[low] = seq[high]\r\n			low += 1\r\n		while low < high and seq[low] <= tmp: low += 1\r\n		if low < high:\r\n			seq[high] = seq[low]\r\n			high -= 1\r\n	seq[low] = tmp\r\n	return low\r\n \r\ndef _quickSort(seq, low, high):\r\n	\"\"\"快速排序辅助函数\"\"\"\r\n	if low >= high: return\r\n	mid = _divide(seq, low, high)\r\n	_quickSort(seq, low, mid - 1)\r\n	_quickSort(seq, mid + 1, high)\r\n \r\ndef quickSort(seq):\r\n	\"\"\"快速排序包裹函数\"\"\"\r\n	size = len(seq)\r\n	_quickSort(seq, 0, size - 1)\r\n	return seq\r\n \r\ndef merge(seq, left, mid, right):\r\n	tmp = []\r\n	i, j = left, mid\r\n	while i < mid and j <= right:\r\n		if seq[i] < seq[j]:\r\n			tmp.append(seq[i])\r\n			i += 1\r\n		else:\r\n			tmp.append(seq[j])\r\n			j += 1\r\n	if i < mid: tmp.extend(seq[i:])\r\n	if j <= right: tmp.extend(seq[j:])\r\n \r\n	seq[left:right+1] = tmp[0:right-left+1]\r\n \r\ndef _mergeSort(seq, left, right):\r\n	if left == right: \r\n		return\r\n	else:\r\n		mid = (left + right) / 2\r\n		_mergeSort(seq, left, mid)\r\n		_mergeSort(seq, mid + 1, right)\r\n		merge(seq, left, mid+1, right)\r\n \r\n#二路并归排序\r\ndef mergeSort(seq):\r\n	size = len(seq)\r\n	_mergeSort(seq, 0, size - 1)\r\n	return seq\r\n \r\nif __name__ == \'__main__\':\r\n	s = [random.randint(0,100) for i in range(0,20)]\r\n	print s\r\n	print \"\\n\"\r\n	print directSelectSort(copy(s))\r\n	print directInsertSort(copy(s))\r\n	print bubbleSort(copy(s))\r\n	print quickSort(copy(s))\r\n	print mergeSort(copy(s))\r\n'),(15,15,19,'代码片段','#index.py   ----    web.py主文件\r\n#!/usr/bin/env python\r\n# -*- coding: utf-8 -*-\r\n\r\nimport web\r\n\r\nurls = (\"/.*\", \"hello\")\r\napp = web.application(urls, globals())\r\n\r\nclass hello:\r\n    def GET(self):\r\n        return \'Hello, world!\'\r\n\r\napp.wsgifunc()'),(16,15,19,'代码片段','#nose_test.py ---- 测试文件脚本\r\n#!/usr/bin/env python\r\n# -*- coding: utf-8 -*-\r\n\r\nimport index\r\n\r\napp = None\r\n\r\nclass TestIndex(object):\r\n    def setUp(self):\r\n        print \'init in class\'\r\n        global app\r\n        self.app = app\r\n    def test_index(self):\r\n        print \'test in class\'\r\n        r = self.app.request(\'/\')\r\n        assert r.status == \'200 OK\'\r\n\r\ndef setUp():\r\n    print \'init in func\'\r\n    global app\r\n    app = index.app\r\n\r\ndef test_index():\r\n    print \'test in func\'\r\n    global app\r\n    r = app.request(\'/\')\r\n    assert r.status == \'200 OK\''),(17,16,19,'baidu_poster.py','#coding: utf-8\n\"\"\"\nAuthor: piglei2007@gmail.com\nVersion: 1.0\n\"\"\"\nimport wx\nimport os\nimport urllib\nimport urllib2\nimport cookielib\nimport simplejson\nimport tempfile\n\nclass LoginError(Exception):\n    \"\"\"登录失败抛出异常\"\"\"\n    pass\n\ndef text_wrapped_by(start, end, content):\n    \"\"\"get the text wrapped by start、end in content\"\"\"\n    si = content.find(start)\n    if si != -1:\n        si += len(start)\n        ei = content.find(end, si)\n        if ei != -1:\n            return content[si:ei]\n    return None\n\nclass Shower(wx.App):\n\n    result = \"\"\n\n    def __init__(self, *args, **kwargs):\n        image = kwargs.pop(\"image\")\n\n        wx.App.__init__(self, *args, **kwargs)\n\n        self.frame = wx.Frame(None, wx.ID_ANY, \"please input\", size=(200, 90))\n        self.frame.Show(True)\n        self.panel = wx.Panel(self.frame, -1, size=(200, 90))\n\n        # image\n        jpg = wx.Image(image, wx.BITMAP_TYPE_JPEG).ConvertToBitmap()\n        wx.StaticBitmap(self.panel, -1, jpg, (10, 10), (jpg.GetWidth(), jpg.GetHeight()))\n\n        # text input\n        self.t = wx.TextCtrl(self.panel, -1, \"\", (10, 55), size=(135, -1))\n        self.t.Bind(wx.EVT_CHAR, self.OnInput)\n        self.t.SetFocus()\n\n        # button\n        self.b = wx.Button(self.panel, 10, \"OK\", (150, 55))\n        self.Bind(wx.EVT_BUTTON, self.OnClick, self.b)\n        self.b.SetSize((40, 26))\n\n    def _submit(self):\n        value = self.t.GetValue()\n        if len(value) == 4:\n            self.result = value\n            self.frame.Close()\n\n    def OnClick(self, event):\n        self._submit()\n\n    def OnInput(self, event):\n        if event.GetKeyCode() == 13:\n            self._submit()\n        event.Skip()\n\n    def start(self):\n        self.MainLoop()\n\nclass BaiduUser(object):\n\n    COOKIE_PATH = \"cookies\"\n        \n    LOGIN_URL = \"https://passport.baidu.com/?login\"\n    LOGIN_IMG_URL = \"https://passport.baidu.com/?verifypic\"\n    POST_URL = \"http://tieba.baidu.com/f/commit/post/add\"\n    THREAD_URL = \"http://tieba.baidu.com/f/commit/thread/add\"\n    TBS_URL = \"http://tieba.baidu.com/dc/common/tbs\"\n    VCODE_URL = \"http://tieba.baidu.com/f/user/json_vcode?lm=%s&rs10=2&rs1=0&t=0.7\"\n    IMG_URL = \"http://tieba.baidu.com/cgi-bin/genimg?%s\"\n\n    LOGIN_ERR_MSGS = {\n        \"1\": \"用户名格式错误，请重新输入\",\n        \"2\": \"用户不存在\",\n        \"3\": \"\",\n        \"4\": \"登录密码错误，请重新输入\",\n        \"5\": \"今日登录次数过多\",\n        \"6\": \"验证码不匹配，请重新输入验证码\",\n        \"7\": \"登录时发生未知错误，请重新输入\",\n        \"8\": \"登录时发生未知错误，请重新输入\",\n        \"16\": \"对不起，您现在无法登录\",\n        \"51\": \'该手机号未通过验证\',\n        \"52\": \'该手机已经绑定多个用户\',\n        \"53\": \'手机号码格式不正确\',\n        \"58\": \'手机号格式错误，请重新输入\',\n        \"256\": \"\",\n        \"257\": \"请输入验证码\",\n        \"20\": \"此账号已登录人数过多\",\n        \"default\": \"登录时发生未知错误，请重新输入\"\n    }\n    POST_ERR_MSGS = {\n        \"38\": \"验证码超时，请重新输入\",\n        \"40\": \"验证码输入错误，请您返回后重新输入\",\n        \"703\": \"为了减少恶意灌水和广告帖，本吧被设置为仅本吧会员才能发贴\",\n        \"704\": \"为了减少恶意灌水和广告帖，本吧被设置为仅本吧管理团队才能发贴，给您带来的不便深表歉意\",\n        \"705\": \"本吧当前只能浏览，不能发贴！\",\n        \"706\": \"抱歉，本贴暂时无法回复。\",\n        \"900\": \"为抵御挖坟危害，本吧吧主已放出贴吧神兽--超级静止蛙，本贴暂时无法回复。\"\n    }\n\n    def __init__(self, username, password):\n        self.username = username\n        self.password = password\n        if not os.path.exists(self.COOKIE_PATH):\n            os.makedirs(self.COOKIE_PATH)\n        self.COOKIE_FILE = os.path.join(self.COOKIE_PATH, str(hash(self.username)))\n        self.cj = cookielib.MozillaCookieJar(self.COOKIE_FILE)\n        self.opener = urllib2.build_opener(urllib2.HTTPCookieProcessor(self.cj))\n        self.opener.addheaders = [\n            (\"User-agent\", \"Mozilla/5.0 (X11; U; FreeBSD i386; en-US; rv:1.9.1) Gecko/20090704 Firefox/3.5\"),\n            (\"Accept\", \"*/*\")\n        ]\n\n    def is_login(self):\n        \"\"\"通过访问TBS判断是否是登录状态\"\"\"\n        content = self.opener.open(self.TBS_URL).read()\n        content = simplejson.loads(content)\n        return content[\"is_login\"]\n        \n    def login(self, verify_code=\"\"):\n        \"\"\"\n        登录\n        \"\"\"\n        print u\"正在登录 %s ...\" % self.username\n        if os.path.exists(self.COOKIE_FILE):\n            try:\n                self.cj.load()\n                print \"发现上次登录的cookie，使用旧cookie.\"\n                if self.is_login():\n                    return\n                print \"cookie已失效，重新登录.\"\n            except Exception, e:\n                pass\n\n        login_params = {\n            \'mem_pass\': \'on\',\n            \'username\': self.username.encode(\"gbk\"),\n            \'password\': self.password,\n            \'verifycode\': verify_code\n        }\n        result = self.opener.open(self.LOGIN_URL, urllib.urlencode(login_params))\n        # 检查是否登录成功\n        if \"USERID\" in [ x.name for x in self.cj ]:\n            self.cj.save()\n            return \n\n        content = result.read().decode(\"gbk\", \"ignore\")\n        body = text_wrapped_by(\'<body onload=\"sett_pwd_load()\">\', \'</body>\', content)\n        err_code =  text_wrapped_by(\"get_err_str(\", \",\", body)\n        # 如果是需要输入验证码\n        if err_code == \'257\':\n            print \"需要输入验证码，重新登录中...\"\n            verify_code = self.open_img(self.LOGIN_IMG_URL)\n            return self.login(verify_code=verify_code)\n        \n        err_msg = self.LOGIN_ERR_MSGS.get(err_code, self.LOGIN_ERR_MSGS[\"default\"])\n        raise LoginError(err_msg)\n\n    def reply(self, url, content):\n        \"\"\"回复帖子\"\"\"\n        print u\"%s 正在发表回帖(%s)...\" % (self.username, url)\n        return self._post(\"post\", url, content)\n\n    def post(self, url, title, content):\n        \"\"\"发布帖子\"\"\"\n        print u\"%s 正在发表主帖(%s)...\" % (self.username, url)\n        return self._post(\"thread\", url, title, content)\n\n    def _post(self, ptype, *args):\n        \"\"\"\n        发表回帖或主贴\n        \"\"\"\n        if ptype == \"thread\":\n            url, title, content = args\n        else:\n            url, content = args\n        post_content = self.opener.open(url).read().decode(\"gbk\", \"ignore\")\n        tieba_name = self.get_tieba_name(post_content)\n        if not tieba_name:\n            return\n\n        tieba_name = tieba_name.encode(\"utf-8\")\n        tbs = self.get_tbs(post_content)\n        tid = self.get_tid(post_content)\n        fid = self.get_fid(post_content)\n        vcode_md5 = self.get_vcode(fid)\n\n        # 获得验证码而且写入本地文件\n        vcode = self.open_img(self.IMG_URL % vcode_md5)\n\n        post_args = {\n            \"hasuploadpic\": 0,\n            \"ie\": \"utf-8\",\n            \"picsign\": \"\",\n            \"rich_text\": 1,\n            \"tfrom\": 1,\n            \"quote_id\": 0,\n            \"user_type\": 0,\n            \"floor_num\": \"\",\n            \"content\": \"%s<br>\" % content,\n            \"kw\": tieba_name, \n            \"fid\": fid,\n            \"tbs\": tbs,\n            \"tid\": tid,\n            \"vcode\": vcode,\n            \"vcode_md5\": vcode_md5,\n        }\n        if ptype == \"thread\":\n            post_args.update(\n                title=title,\n                useSignName=\"on\",\n                floor_num=0\n            )\n        ret = self.opener.open(self.POST_URL if ptype == \"post\" else self.THREAD_URL, urllib.urlencode(post_args))\n        ret = simplejson.loads(ret.read())\n        ret_no = ret[\"no\"]\n        if ret_no == 0:\n            print \"发帖成功！(%s)...\" % url\n            return\n        err_msg = self.POST_ERR_MSGS.get(str(ret_no))\n        if err_msg:\n            print err_msg\n        else:\n            print \"发帖失败。(%s)...\" % ret_no\n\n        if ret_no in (38, 40):\n            return self._post(ptype, *args)\n\n    @staticmethod\n    def get_tieba_name(content):\n        ret = text_wrapped_by(\"<title>\", u\"吧_贴吧\", content)\n        if ret:\n            return ret.split(\"_\")[-1]\n        return None\n\n    @staticmethod\n    def get_tbs(content):\n        return text_wrapped_by(\'tbs:\"\', \'\"\', content)\n\n    @staticmethod\n    def get_tid(content):\n        ret = text_wrapped_by(\"tid:\'\", \"\'\", content)\n        return ret\n\n    @staticmethod\n    def get_fid(content):\n        ret = text_wrapped_by(\"fid:\'\", \"\'\", content)\n        return ret\n\n    def get_vcode(self, fid):\n        content = self.opener.open(self.VCODE_URL % fid).read()\n        content = simplejson.loads(content)\n        return content[\'data\'][\'vcodestr\']\n\n    def open_img(self, url):\n        \"\"\"\n        获得验证码\n        \"\"\"\n        img = self.opener.open(url)\n        content = img.read()\n        if not content:\n            return \"\"\n\n        f = tempfile.NamedTemporaryFile()\n        f.write(content)\n        f.flush()\n        \n        s = Shower(False, image=f.name)\n        s.start()\n\n        return s.result.encode(\"utf-8\")\n\nif __name__ == \'__main__\':\n    username = u\"用户名\"\n    password = \"password\"\n    u = BaiduUser(username, password)\n    u.login()\n    # 发表主贴\n    u.post(\"http://tieba.baidu.com/f?kw=asdfasdf\", \"this is a test(2)\", \"Hello, world.\")\n    # 发表回复\n    u.reply(\"http://tieba.baidu.com/p/1150567896\", \"test\")\n'),(18,17,19,'tongji.py','#encoding:utf-8\r\n#author:TY (http://pythoner.net)\r\n#email:master@t-y.me\r\n#date:2011-09-10 15:06\r\nimport os\r\n\r\nclass TongJi(object):\r\n\r\n    def __init__(self,path=\'/\'):\r\n        self.file_suffix = [\'py\'] # 要统计的文件后缀\r\n        self.exclude_dir = [\'cache\',\'templates\',\'weibopy\',\'media\',\'static\',\'.idea\',\'.svn\']\r\n        self.exclude_suffix = [\'pyc\',\'xml\']\r\n        self.exclude_file = [\'__init__.py\',\'settings.py\',\'tests.py\']\r\n        self.root_path = path\r\n        self.current_dir = \'\'\r\n        self.count = 0\r\n        \r\n    def run(self):\r\n        for current in os.listdir(self.root_path):\r\n            if not os.path.isdir(current):\r\n                continue\r\n            elif current in self.exclude_dir:\r\n                continue\r\n            self.current_dir = current\r\n            self.walk()\r\n        print \'Total lines:%d\' %self.count\r\n\r\n    def walk(self):\r\n        for root,dirs,files in os.walk(os.path.join(self.root_path,self.current_dir)):\r\n            for file_name in files:\r\n                \r\n                if str(file_name).split(\'.\')[-1] in self.file_suffix and file_name not in self.exclude_file:\r\n                    self.line_count(os.path.join(root,file_name))\r\n\r\n    def line_count(self,file_path):\r\n        count = len(open(file_path,\'rU\').readlines())\r\n        print \'[file]%s \\n[line]%d \\n\' %(file_path.replace(\'\\\\\',\'/\'),count)\r\n        self.count += count\r\n        \r\n\r\ntj = TongJi(path=\"d:/pythoner\")\r\n\r\ntj.exclude_dir = [\'cache\',\'templates\',\'weibopy\',\'media\',\'static\',\'.idea\',\'.svn\'] # 不参与统计的文件夹\r\ntj.exclude_file = [\'__init__.py\',\'settings.py\',\'tests.py\'] # 不参与统计的文件\r\n\r\ntj.run()\r\n'),(19,17,19,'代码片段','运行结果\r\nIDLE 1.2.2      ==== No Subprocess ====\r\n>>> \r\n[file]d:/pythoner/accounts/admin.py \r\n[line]11 \r\n\r\n[file]d:/pythoner/accounts/forms.py \r\n[line]17 \r\n\r\n[file]d:/pythoner/accounts/models.py \r\n[line]32 \r\n\r\n[file]d:/pythoner/accounts/sina.py \r\n[line]100 \r\n\r\n[file]d:/pythoner/accounts/urls.py \r\n[line]14 \r\n\r\n[file]d:/pythoner/accounts/views.py \r\n[line]288 \r\n\r\n[file]d:/pythoner/accounts/signals.py \r\n[line]7 \r\n\r\n[file]d:/pythoner/accounts/templatetags/accounts_tags.py \r\n[line]26 \r\n\r\n[file]d:/pythoner/code/m