\r\n[line]23 \r\n\r\n[file]d:/pythoner/main/verify/views.py \r\n[line]92 \r\n\r\n[file]d:/pythoner/main/templatetags/admin_lib.py \r\n[line]17 \r\n\r\n[file]d:/pythoner/main/templatetags/main_tags.py \r\n[line]14 \r\n\r\n[file]d:/pythoner/pm/admin.py \r\n[line]12 \r\n\r\n[file]d:/pythoner/pm/forms.py \r\n[line]9 \r\n\r\n[file]d:/pythoner/pm/models.py \r\n[line]148 \r\n\r\n[file]d:/pythoner/pm/urls.py \r\n[line]13 \r\n\r\n[file]d:/pythoner/pm/views.py \r\n[line]248 \r\n\r\n[file]d:/pythoner/pm/templatetags/pm_tags.py \r\n[line]15 \r\n\r\n[file]d:/pythoner/topic/admin.py \r\n[line]13 \r\n\r\n[file]d:/pythoner/topic/forms.py \r\n[line]9 \r\n\r\n[file]d:/pythoner/topic/models.py \r\n[line]83 \r\n\r\n[file]d:/pythoner/topic/urls.py \r\n[line]19 \r\n\r\n[file]d:/pythoner/topic/views.py \r\n[line]292 \r\n\r\n[file]d:/pythoner/topic/feed.py \r\n[line]19 \r\n\r\n[file]d:/pythoner/topic/signals.py \r\n[line]7 \r\n\r\n[file]d:/pythoner/topic/templatetags/topic_tags.py \r\n[line]62 \r\n\r\n[file]d:/pythoner/wiki/admin.py \r\n[line]15 \r\n\r\n[file]d:/pythoner/wiki/models.py \r\n[line]83 \r\n\r\n[file]d:/pythoner/wiki/urls.py \r\n[line]17 \r\n\r\n[file]d:/pythoner/wiki/views.py \r\n[line]154 \r\n\r\n[file]d:/pythoner/wiki/forms.py \r\n[line]14 \r\n\r\n[file]d:/pythoner/wiki/feed.py \r\n[line]22 \r\n\r\n[file]d:/pythoner/wiki/templatetags/wiki_tags.py \r\n[line]41 \r\n\r\nTotal lines:4003'),(20,18,19,'代码片段','#coding: utf-8\r\n\'\'\'\r\n春运查询火车票转让信息\r\nAuthor: piglei2007@gmail.com\r\nDate: 2011.01.25\r\n\'\'\'\r\nimport re\r\nimport os\r\nimport time\r\nimport urlparse\r\nimport datetime\r\nimport traceback\r\nimport urllib2\r\nimport socket\r\nsocket.setdefaulttimeout(20)\r\n \r\nBLANK_RE = re.compile(r\"\\s+\")\r\n \r\nopener = urllib2.build_opener(urllib2.HTTPCookieProcessor())\r\nopener.addheaders = [\r\n    (\"User-agent\", \"Mozilla/5.0 (X11; U; FreeBSD i386; en-US; rv:1.9.1) Gecko/20090704 Firefox/3.5\"),\r\n    (\"Accept\", \"*/*\"),\r\n]\r\nurllib2.install_opener(opener)\r\n \r\nfrom BeautifulSoup import BeautifulSoup\r\n \r\nSOURCE = {\r\n    \"58\": \"http://bj.58.com/huochepiao/?Num=%(train)s&StartTime=%(date)s00\",\r\n    \"ganji\": \"http://bj.ganji.com/piao/cc_%(train)s/%(date)s/\",\r\n}\r\nRECORD_FILE = \"/tmp/ticket_records.txt\"\r\n \r\ndef parse_record():\r\n    try:\r\n        return set([x.strip() for x in open(RECORD_FILE, \"r\").readlines()])\r\n    except IOError:\r\n        open(RECORD_FILE, \"w\")\r\n        return set()\r\n \r\ndef flush_record(records):\r\n    open(RECORD_FILE, \"w\").write(\"\\n\".join(records))\r\n \r\ndef main(config):\r\n    \"\"\"\r\n    开始抓取\r\n    \"\"\"\r\n    existed = parse_record()\r\n    to_email = []\r\n \r\n    for train in config[\"trains\"]:\r\n        for date in config[\"dates\"]:\r\n            for type, _url in SOURCE.items():\r\n                url = _url % dict(train=train, date=date)\r\n                content = urllib2.urlopen(url).read()\r\n                soup = BeautifulSoup(content)\r\n                result = parse_content(type, soup, train)\r\n                for url, text in result:\r\n                    url = urlparse.urljoin(_url, url)\r\n                    # 只要卧铺！\r\n                    if url not in existed and u\"卧\" in text:\r\n                        to_email.append([text, url])\r\n                    existed.add(url)\r\n    if to_email:\r\n        content = \"\".join(\r\n            [x for x in [\" | \".join(y) for y in to_email]]\r\n        ).encode(\"utf-8\")\r\n        simple_mail(config[\"people\"], content)\r\n    flush_record(existed)\r\n \r\ndef parse_content(type, soup, train):\r\n    \"\"\"\r\n    获得车次信息\r\n    \"\"\"\r\n    result = []\r\n    if type == \"58\":\r\n        info_table = soup.find(\"table\", id=\"infolist\")\r\n        if info_table:\r\n            for x in info_table.findAll(\"tr\", text=re.compile(ur\"%s(?!时刻表)\" % train, re.I)):\r\n                a = x.parent\r\n                _text = BLANK_RE.sub(\"\", a.text)\r\n                result.append([a[\"href\"], _text])\r\n    if type == \"ganji\":\r\n        for x in soup.findAll(\"dl\", {\"class\": \"list_piao\"}):\r\n            a = x.dt.a\r\n            result.append([a[\"href\"], a.text])\r\n    return result\r\n \r\nEMAIL_HOST = \'smtp.sohu.com\'\r\nEMAIL_HOST_USER = \'yourname@sohu.com\'\r\nEMAIL_HOST_PASSWORD = \'yourpassword\'\r\nEMAIL_PORT = 25\r\n \r\ndef simple_mail(to, content):\r\n    \"\"\"\r\n    发送邮件\r\n    \"\"\"\r\n    import smtplib\r\n    from email.mime.text import MIMEText\r\n \r\n    msgRoot = MIMEText(content, \'html\', \'UTF-8\')\r\n    msgRoot[\'Subject\'] = \"[%s]有票来啦！！！！\" % datetime.datetime.today().isoformat(\" \")\r\n    msgRoot[\'From\'] = EMAIL_HOST_USER\r\n    msgRoot[\'To\'] = \", \".join(to)\r\n \r\n    s = smtplib.SMTP(EMAIL_HOST, EMAIL_PORT)\r\n    s.login(EMAIL_HOST_USER, EMAIL_HOST_PASSWORD)\r\n    s.sendmail(EMAIL_HOST_USER, to, msgRoot.as_string())\r\n    s.close()\r\n \r\ndef switch_time_zone():\r\n    \"\"\"\r\n    切换时区\r\n    \"\"\"\r\n    os.environ[\"TZ\"] = \"Asia/Shanghai\"\r\n    time.tzset()\r\n \r\nswitch_time_zone()\r\n \r\nif __name__ == \'__main__\':\r\n    config = {\r\n        \"trains\": (\"k471\",),\r\n        \"dates\": (\"20110129\",),\r\n        \"people\": (\r\n            \"youremail@sohu.com\",\r\n        )\r\n    }\r\n    try:\r\n        main(config)\r\n        print \"%s: ok\" % datetime.datetime.today()\r\n    except Exception, e:\r\n        print '),(21,19,19,'spider.py','#encoding: utf-8\r\n\"\"\"\r\nauthor:TY (http://pythoner.net)\r\nemail:master@t-y.me\r\ndate:2011-09-11\r\nverison:0.1.0\r\n\"\"\"\r\n\r\nimport time,math,os,re,urllib,urllib2,cookielib\r\nfrom BeautifulSoup import BeautifulSoup\r\nimport time\r\n\r\n\r\n\"\"\" 自动抓取百度图片 \"\"\"\r\nclass BaiduImage:\r\n    \"\"\"\r\n    image_links     图片URL链接\r\n    image_dir       图片存放文件夹\r\n    current_page    当前页面地址\r\n    next_page       下一页面地址\r\n    image_count     已下载图片数量\r\n    \"\"\"\r\n    \r\n    image_links = []\r\n    image_dir = \'image\'\r\n    current_page = \'\'\r\n    next_page = \'\'\r\n    image_count = 0\r\n    \r\n    def __init__(self):\r\n        self.cj =cookielib.LWPCookieJar()\r\n        try:\r\n            self.cj.revert(\'baiduimage.cookie\')\r\n        except:\r\n            None        \r\n        self.opener = urllib2.build_opener(urllib2.HTTPCookieProcessor(self.cj))\r\n        urllib2.install_opener(self.opener)\r\n        self.opener.addheaders = [\r\n            (\"User-agent\", \"Mozilla/5.0 (X11; U; FreeBSD i386; en-US; rv:1.9.1) Gecko/20090704 Firefox/3.5\"),\r\n            (\"Accept\", \"*/*\")]\r\n\r\n\r\n    \"\"\" 得到当前页面中图片的链接地址 \"\"\"\r\n    def get_image_links(self):\r\n        try:\r\n            html = self.opener.open(self.current_page).read()\r\n        except Exception,e:\r\n            self.write_log(e)\r\n            return\r\n            \r\n        soup = BeautifulSoup(html)\r\n        self.image_links = []\r\n        for link in soup.findAll(\'a\',{\'href\':re.compile(\'^./img\')}):\r\n            if \'src=http://\' in str(link):\r\n                l = re.findall(r\'src=(http://.*)\',link[\'href\'])[0]\r\n                self.image_links.append(l)\r\n                \r\n                \r\n    \"\"\" 得到下一页地址 \"\"\"\r\n    def get_next_page(self):\r\n        html = self.opener.open(self.current_page).read()\r\n        soup = BeautifulSoup(html)\r\n        spans = soup.findAll(\'span\')\r\n        for span in spans:\r\n            span_html = str(span)\r\n            if \'下一页\' in span_html:\r\n                self.current_page = str(\'http://wap.baidu.com\')+str(BeautifulSoup\r\r\n(span_html).find(\'a\')[\'href\'])\r\n                self.write_log(\'Going next page...\')\r\n                return                \r\n\r\n        self.write_log(\'This is the latest page\')\r\n        self.next_page = \'\'\r\n        return False\r\n        \r\n\r\n    \"\"\" 下载self.image_links中的图片 \"\"\"\r\n    def download(self):\r\n        if not self.image_links:\r\n            return False\r\n        \r\n        self.write_log(\'Current page - %s\' %self.current_page)\r\n\r\n        for link in self.image_links:\r\n            try:\r\n                data = urllib.urlopen(link).read()\r\n            except Exception,e:\r\n                self.write_log(\'Connect error:%s\' %e)\r\n                return\r\n            self.write_log(\'Downloading... - %s\' %link)                \r\n            file_name = str(int(time.time()))+\'.jpg\'\r\n            file_path = os.path.join(self.image_dir,file_name)\r\n            image = open(file_path,\'wb\')\r\n            \r\n            try:                \r\n                image.write(data)\r\n            except Exception,e:\r\n                self.write_log(\'Download faild:%s\' %e)\r\n            else:\r\n                self.write_log(\'Download Success!-%s\' %link)\r\n            \r\n            self.image_count += 1\r\n            image.close()\r\n            del image\r\n            time.sleep(2)\r\n\r\n    def write_log(self,text):\r\n        os.system(\'cls\')\r\n        print text\r\n        log = open(\'log.txt\',\'a\')\r\n        log.write(text)\r\n        log.write(\'\\n\')\r\n        log.close()          \r\n\r\n    \r\n    \"\"\" 给出wap起始页开始下载 \"\"\"\r\n    def run(self,start_page):\r\n        self.current_page = start_page\r\n\r\n        while True:\r\n            # 获取后下载首页图片\r\n            self.get_image_links()\r\n            self.download()\r\n            self.get_next_page()\r\n            self.write_log(\'Image total:%d\' %self.image_count)\r\n            time.sleep(1)\r\n            \r\n\r\napp = BaiduImage()\r\napp.run(start_page=\'http://wap.baidu.com/ssid=0/from=0/bd_page_type=1/uid=wiaui_1315707661_2623/pu=sz%40224_220%2Cusm%401/img?tn=bdwis&word=%E8%8B%8D%E4%BA%95%E7%A9%BA&pn=12&dw=w240&bs=176_208&pinf=6_6_0_@bdwis_@%E8%8B%8D%E4%BA%95%E7%A9%BA_@176_208_@w240&sp=&mid=w240\')\r\n'),(22,20,19,'代码片段','_                                 =\\\r\n      