    raise \"file dos not exist.\" \r\n        return None \r\n    if path.exists(bmp):  \r\n        unlink(bmp)  \r\n    im = Image.open(infile)  \r\n    im.save(bmp,\'BMP\')  \r\n    return bmp  \r\n \r\ndef SetWallPaper1(wallpaper, Style=0):      \r\n    try:  \r\n        iad=pythoncom.CoCreateInstance(shell.CLSID_ActiveDesktop, None, pythoncom.CLSCTX_INPROC_SERVER, shell.IID_IActiveDesktop)  \r\n        opts=iad.GetDesktopItemOptions()  \r\n        Reserved=0 \r\n        iad.SetWallpaper(wallpaper, Reserved)  \r\n        iad.SetWallpaperOptions(Style,Reserved)  \r\n        iad.ApplyChanges(0xffff)  \r\n    except:  \r\n        return False \r\n    else:  \r\n        return True \r\n \r\ndef SetWallpaper2( jpgfile ):  \r\n    bmp = jpg2bmp(jpgfile)  \r\n    k = win32api.RegOpenKeyEx(win32con.HKEY_CURRENT_USER,\"Control PanelDesktop\",0,win32con.KEY_SET_VALUE)  \r\n    win32api.RegSetValueEx(k, \"WallpaperStyle\", 0, win32con.REG_SZ, \"0\")  \r\n    win32api.RegSetValueEx(k, \"TileWallpaper\", 0, win32con.REG_SZ, \"0\")  \r\n    win32gui.SystemParametersInfo(win32con.SPI_SETDESKWALLPAPER, bmp, 1+2) '),(24,22,19,'代码片段',' \r\n   #coding=gb18030  \r\n    \r\n   import sys,threading,time  \r\n   import serial  \r\n   import binascii,encodings  \r\n   import re  \r\n   import socket  \r\n   from struct import *  \r\n   class ComThread:   \r\n       def __init__(self, Port=0):   \r\n           self.l_serial = None \r\n           self.alive = False \r\n           self.waitEnd = None \r\n           self.port = Port  \r\n    \r\n       def waiting(self):   \r\n           if not self.waitEnd is None:   \r\n               self.waitEnd.wait()  \r\n    \r\n       def SetStopEvent(self):   \r\n           if not self.waitEnd is None:   \r\n               self.waitEnd.set()  \r\n           self.alive = False \r\n           self.stop()  \r\n    \r\n       def start(self):   \r\n           self.l_serial = serial.Serial();   \r\n           self.l_serial.port = self.port  \r\n           self.l_serial.baudrate = 9600 \r\n           self.l_serial.timeout = 2 \r\n           self.l_serial.open()  \r\n           if self.l_serial.isOpen():  \r\n               self.waitEnd = threading.Event()  \r\n               self.alive = True \r\n               self.thread_read = None \r\n               self.thread_read = threading.Thread(target=self.FirstReader)  \r\n               self.thread_read.setDaemon(1)  \r\n               self.thread_read.start()  \r\n               return True \r\n           else:   \r\n               return False \r\n    \r\n       def FirstReader(self):   \r\n           while self.alive:   \r\n               # 接收间隔  \r\n               time.sleep(0.1)  \r\n               try:   \r\n                   data = \'\' \r\n                   n = self.l_serial.inWaiting()  \r\n                   if n:   \r\n                       data = data + self.l_serial.read(n)  \r\n                       for l in xrange(len(data)):  \r\n                           print \'%02X\' % ord(data[l]),  \r\n    \r\n                       # 发送数据  \r\n                       snddata = \'\' \r\n                       snddata += chr(97)  \r\n                       tt = 0x12345678 \r\n                       snddata += pack(\'i\', tt)  \r\n                       snddata += chr(0x64)  \r\n                       self.l_serial.write(snddata)  \r\n                   # 判断结束  \r\n                   if len(data) &gt; 0 and ord(data[len(data)-1])==0x45:   \r\n                       #pass  \r\n                       break \r\n                     \r\n               except Exception, ex:   \r\n                   print str(ex)  \r\n    \r\n           self.waitEnd.set();   \r\n           self.alive = False \r\n    \r\n       def stop(self):   \r\n           self.alive = False \r\n           self.thread_read.join()  \r\n           if self.l_serial.isOpen():   \r\n               self.l_serial.close()  \r\n      \r\n   #测试用部分   \r\n   if __name__ == \'__main__\':   \r\n       rt = ComThread()  \r\n       try:   \r\n           if rt.start():   \r\n               rt.waiting()  \r\n               rt.stop()  \r\n           else:   \r\n               pass;               \r\n       except Exception,se:   \r\n           print str(se)  \r\n        \r\n       if rt.alive:   \r\n           rt.stop()  \r\n    \r\n       print \'\' \r\n       print \'End OK .\' \r\n       del rt \r\n'),(25,23,19,'代码片段','#!/usr/bin/env python\r\n#  corestats.py (COREy STATS)\r\n#  Copyright (c) 2006-2007, Corey Goldberg (corey@goldb.org)\r\n#\r\n#    statistical calculation class\r\n#    for processing numeric sequences\r\n#\r\n#  license: GNU LGPL\r\n#\r\n#  This library is free software; you can redistribute it and/or\r\n#  modify it under the terms of the GNU Lesser General Public\r\n#  License as published by the Free Software Foundation; either\r\n#  version 2.1 of the License, or (at your option) any later version.\r\n\r\nimport sys\r\n\r\nclass Stats:\r\n\r\n    def __init__(self, sequence):\r\n        # sequence of numbers we will process\r\n        # convert all items to floats for numerical processing\r\n        self.sequence = [float(item) for item in sequence]\r\n\r\n    def sum(self):\r\n        if len(self.sequence) < 1:\r\n            return None\r\n        else:\r\n            return sum(self.sequence)\r\n\r\n    def count(self):\r\n        return len(self.sequence)\r\n\r\n    def min(self):\r\n        if len(self.sequence) < 1:\r\n            return None\r\n        else:\r\n            return min(self.sequence)\r\n\r\n    def max(self):\r\n        if len(self.sequence) < 1:\r\n            return None\r\n        else:\r\n            return max(self.sequence)\r\n\r\n    def avg(self):\r\n        if len(self.sequence) < 1:\r\n            return None\r\n        else:\r\n            return sum(self.sequence) / len(self.sequence)    \r\n\r\n    def median(self):\r\n        if len(self.sequence) < 1:\r\n            return None\r\n        else:\r\n            self.sequence.sort()\r\n            return self.sequence[len(self.sequence) // 2]\r\n\r\n    def stdev(self):\r\n        if len(self.sequence) < 1:\r\n            return None\r\n        else:\r\n            avg = self.avg()\r\n            sdsq = sum([(i - avg) ** 2 for i in self.sequence])\r\n            stdev = (sdsq / (len(self.sequence) - 1)) ** .5\r\n            return stdev\r\n\r\n    def percentile(self, percentile):\r\n        if len(self.sequence) < 1:\r\n            value = None\r\n        elif (percentile >= 100):\r\n            sys.stderr.write(\'ERROR: percentile must be < 100.  you supplied: %s\\n\'% percentile)\r\n            value = None\r\n        else:\r\n            element_idx = int(len(self.sequence) * (percentile / 100.0))\r\n            self.sequence.sort()\r\n            value = self.sequence[element_idx]\r\n        return value\r\n\r\n# Sample script using this class:\r\n# -------------------------------------------\r\n#    #!/usr/bin/env python\r\n#    import corestats\r\n#\r\n#    sequence = [1, 2.5, 7, 13.4, 8.0]\r\n#    stats = corestats.Stats(sequence)\r\n#    print stats.avg()\r\n#    print stats.percentile(90)\r\n# -------------------------------------------\r\n'),(27,25,19,'代码片段','#encoding:utf-8\r\n\"\"\"\r\n示例代码\r\n\"\"\"\r\nfrom Crypto.Cipher import AES\r\n    key = \'0123456789abcdef\'\r\n    mode = AES.MODE_CBC\r\n    encryptor = AES.new(key, mode)\r\n    text = \'j\' * 64 + \'i\' * 128\r\n    ciphertext = encryptor.encrypt(text)\r\n\r\n\"\"\"\r\n上例中的key是16位, 还可以是24 或 32 位长度， 其对应为 AES-128, AES-196 和 AES-256.\r\n解密则可以用以下代码进行：\r\n\"\"\"\r\n\r\n#decryptor = AES.new(key, mode)\r\n#plain = decryptor.decrypt(ciphertext)'),(28,26,19,'代码片段','# copyright (c) 1993-1999 microsoft corp.\r\n#\r\n# this is a sample hosts file used by microsoft tcp/ip for windows.\r\n#\r\n# this file contains the mappings of ip addresses to host names. each\r\n# entry should be kept on an individual line. the ip address should\r\n# be placed in the first column followe