  80;\r\n        server_name  localhost;\r\n\r\n        #charset koi8-r;\r\n\r\n        #access_log  logs/host.access.log  main;\r\n\r\n        location / {\r\n            root   html;\r\n            index  index.html index.htm;\r\n			\r\n            # host and port to fastcgi server\r\n            fastcgi_pass 127.0.0.1:55880;\r\n            fastcgi_param PATH_INFO $fastcgi_script_name;\r\n            fastcgi_param REQUEST_METHOD $request_method;\r\n            fastcgi_param QUERY_STRING $query_string;\r\n            fastcgi_param SERVER_NAME $server_name;\r\n            fastcgi_param SERVER_PORT $server_port;\r\n            fastcgi_param SERVER_PROTOCOL $server_protocol;\r\n            fastcgi_param CONTENT_TYPE $content_type;\r\n            fastcgi_param CONTENT_LENGTH $content_length;\r\n            fastcgi_pass_header Authorization;\r\n            fastcgi_intercept_errors off;			\r\n			\r\n        }\r\n\r\n        #error_page  404              /404.html;\r\n\r\n        # redirect server error pages to the static page /50x.html\r\n        #\r\n        error_page   500 502 503 504  /50x.html;\r\n        location = /50x.html {\r\n            root   html;\r\n        }\r\n\r\n        # proxy the PHP scripts to Apache listening on 127.0.0.1:80\r\n        #\r\n        #location ~ \\.php$ {\r\n        #    proxy_pass   http://127.0.0.1;\r\n        #}\r\n\r\n        # pass the PHP scripts to FastCGI server listening on 127.0.0.1:9000\r\n        #\r\n        #location ~ \\.php$ {\r\n        #    root           html;\r\n        #    fastcgi_pass   127.0.0.1:9000;\r\n        #    fastcgi_index  index.php;\r\n        #    fastcgi_param  SCRIPT_FILENAME  /scripts$fastcgi_script_name;\r\n        #    include        fastcgi_params;\r\n        #}\r\n\r\n        # deny access to .htaccess files, if Apache\'s document root\r\n        # concurs with nginx\'s one\r\n        #\r\n        #location ~ /\\.ht {\r\n        #    deny  all;\r\n        #}\r\n    }\r\n\r\n\r\n    # another virtual host using mix of IP-, name-, and port-based configuration\r\n    #\r\n    #server {\r\n    #    listen       8000;\r\n    #    listen       somename:8080;\r\n    #    server_name  somename  alias  another.alias;\r\n\r\n    #    location / {\r\n    #        root   html;\r\n    #        index  index.html index.htm;\r\n    #    }\r\n    #}\r\n\r\n\r\n    # HTTPS server\r\n    #\r\n    #server {\r\n    #    listen       443;\r\n    #    server_name  localhost;\r\n\r\n    #    ssl                  on;\r\n    #    ssl_certificate      cert.pem;\r\n    #    ssl_certificate_key  cert.key;\r\n\r\n    #    ssl_session_timeout  5m;\r\n\r\n    #    ssl_protocols  SSLv2 SSLv3 TLSv1;\r\n    #    ssl_ciphers  HIGH:!aNULL:!MD5;\r\n    #    ssl_prefer_server_ciphers   on;\r\n\r\n    #    location / {\r\n    #        root   html;\r\n    #        index  index.html index.htm;\r\n    #    }\r\n    #}\r\n\r\n}\r\n'),(30,29,19,'代码片段','#!/usr/bin/env python3\r\n# vim:fileencoding=utf-8\r\n\r\nimport os\r\nimport time\r\nimport select\r\n\r\nfd = os.open(\'test\', os.O_NONBLOCK | os.O_RDONLY)\r\nwhile True:\r\n  if not select.select([fd], [], [], 1)[0]:\r\n    print(\'waiting...\')\r\n  else:\r\n    got = os.read(fd, 1024).decode().rstrip()\r\n    if not got:\r\n      os.close(fd)\r\n      fd = os.open(\'test\', os.O_NONBLOCK | os.O_RDONLY)\r\n    else:\r\n      print(\'got\', got)'),(31,30,19,'代码片段','#!/usr/bin/python \r\nimport urllib \r\nimport re \r\nfrom sgmllib import SGMLParser \r\nclass URLLister(SGMLParser): \r\n    def reset(self): \r\n      self.urls = [] \r\n      SGMLParser.reset(self) \r\n    def start_a(self, attrs): \r\n        href = [v for k, v in attrs if k==\'href\'] \r\n        if href: \r\n            self.urls.extend(href) \r\ndef doUrls(url): \r\n  print \"hehe\\tURL:\\t\"+url \r\ndef crawlWeb(web_url): \r\n  web_url_s = web_url.split(\"/\") \r\n  doUrls(web_url) \r\n  lenW=len(web_url_s) \r\n  ToCrawlUrls = [] \r\n  ToCrawlUrls_temp=[] \r\n  BeCrawledUrls = [] \r\n  ToCrawlUrls.append(web_url) \r\n  while(len(ToCrawlUrls) !=0): \r\n    #print ToCrawlUrls \r\n    #print \"\\t\\tok\" \r\n    for i_url in ToCrawlUrls: \r\n      #print \"parsing\\t\" + i_url \r\n      raw_html = urllib.urlopen(i_url).read() \r\n      lister=URLLister() \r\n      lister.feed(raw_html) \r\n      temp_urls = lister.urls \r\n      for j_url in temp_urls: \r\n        if(re.search(r\"http:\",j_url)!=None and (re.search(r\'html \r\n$\',j_url)!=None or re.search(r\'htm$\',j_url)!=None)): \r\n           j_url=j_url \r\n        elif(re.search(r\'html$\',j_url)!=None or re.search(r\'htm \r\n$\',j_url)!=None): \r\n          if(re.search(r\"^\\.\",j_url)==None): \r\n            j_url=i_url+j_url \r\n          else: \r\n            continue \r\n        else: \r\n          continue \r\n        j_url_s = j_url.split(\"/\") \r\n        lenJ = len(j_url_s) \r\n        if(BeCrawledUrls.count(j_url)==0): \r\n          if(lenJ >= lenW): \r\n            #print \"\\t\"+j_url+\"\\n\\t\"+i_url \r\n            if(re.search(i_url,j_url)!=None): \r\n              ToCrawlUrls_temp.append(j_url) \r\n              doUrls(j_url) \r\n      BeCrawledUrls.append(i_url) \r\n    ToCrawlUrls = ToCrawlUrls_temp \r\n    ToCrawlUrls_temp = [] \r\ncrawlWeb(\"http://www.tianyabook.com/hyzpj/hyzpj/wx/jingyong/xajh\") '),(32,31,19,'shingling.py','#encoding:utf-8\r\n#author:ty\r\n#site:http://pythoner.net\r\n#date:2011-10-28\r\n\"\"\"\r\n层叠发计算文章相似度\r\n实例文本：用层叠发计算文本相似度有什么好处\r\n设切片长度为5，则其切片如下：\r\n用层叠发计->层叠发计算->叠发计算文->发计算文本...->有什么好处\r\n\"\"\"\r\n\r\nclass ShingLing(object):\r\n    \"\"\"\r\n    层叠法计算文本相似度\r\n    \"\"\"\r\n    cut_step = 10  #切片字长\r\n    text = []\r\n    com_count = 0\r\n    \r\n    def __init__(self,text1,text2):\r\n        self._cut(text1)\r\n        self._cut(text2)\r\n\r\n    def _cut(self,text):\r\n        if len(text) < self.cut_step:\r\n            return\r\n        \r\n        text = list(text)\r\n        pice_list = []\r\n        for i in range(len(text) - self.cut_step):\r\n            pice = text[i:i + self.cut_step]\r\n            pice_list.append(pice)\r\n        re = {\'pice\':pice_list,\'length\':len(text)-self.cut_step}\r\n        self.text.append(re)\r\n\r\n    def com(self):\r\n        pice1 = self.text[0][\'pice\']\r\n        pice2 = self.text[1][\'pice\']\r\n\r\n        for item in pice1:\r\n            if item in pice2:\r\n                self.com_count += 1\r\n\r\n        total_length = self.text[0][\'length\'] + self.text[1][\'length\']\r\n        com_count = self.com_count*2\r\n        return com_count/float(total_length)\r\n\r\ntext1 = \"\"\"请检查您的互联网连接状况，重新启动您可能正在使用的任何路由器、调制解调器以及其他网络设备。\r\n检查您的 DNS 设置。如果您不确定是什么意思，请与您的网络管理员联系。\r\n要尝试停用网络预测，请按以下步骤操作：转至扳手菜单 > 选项 > 高级选项，然后取消选中“预测网络操作，以提高网页载入速度”。如果该问题仍未得到解决，建议您重新选中此选项以提\"\"\"\r\ntext2 = \"\"\"请检查您的互联网连接状况，重新启动您可能正在使用的任何路由器、调制解调器以及其他网络设备。\r\n检查您的 DNS 设置。如果您不确定是什么意思，请与您的网络管理员联系\"\"\"\r\ns = ShingLing(text1,text2)\r\n\r\nprint s.com()\r\n\r\n'),(33,32,19,'mid.py','#encoding:utf-8\r\n#author:ty\r\n#site:http://pythoner.net\r\n#date:2011-11-01\r\n\r\n\"\"\"\r\n二分法查找\r\n折半查找法是效率较高的一种查找方法。假设有已经按照从小到大的顺序排列好的五个整数a[0]~a[4]，要查找的数是X，其基本思想是：\r\n设查找数据的范围下限为l=1，上限为h=5，求中点m=（1+h）/2，用X与中点元素a[m]比较，若X等于a[m]，即找到，停止查找；否则，若X大于a[m]，替换下限l=m+1，到上半段继续查找；若X小于a[m]，换上限h=m-1，到上半段继续查找；如此重复前面的过程直到找到或者l>h为止。如果l>h，说明没有此数，打印找不到信息，程序结束。\r\n\"\"\"\r\n\r\na = [x for x in range(100)]\r\nx = 21\r\n\r\ndef midfind(a,x):\r\n    min_index = 0\r\n    max_index = len(a) - 1\r\n\r\n    print a\r\n    while True:\r\n        mid_index = (min_index+max_index)/2\r\n\r\n        print \'min:\',a[min_index],\r\n        print \'max:\',a[max_index],\r\n        print \'mid:\',a[mid_index]\r\n            \r\n        if x == a[mid_index]:\r\n            return x\r\n        elif x < a[mid_index]:\r\n            max_index = mid_index - 1\r\n        elif x > a[mid_index]:\r\n            min_index = mid_index + 1\r\n    \r\nprint midfind(a,x)\r\n'),(34,33,19,'代码片段','# -*- coding: UTF8 -*-\r\nimport sys\r\nimport copy\r\n\r\ndef init_pass(T):\r\n    C = {}\r\n    for t in T:\r\n        for i in t:\r\n            if i in C.keys():\r\n                C[i] += 1\r\n            else:\r\n                C[i] = 1\r\n    return C\r\n\r\ndef candidate_gen(F):\r\n    C = []\r\n    k = len(F[0]) + 1\r\n    for f1 in F:\r\n        for f2 in F:\r\n            if f1[k-2] < f2[k-2]:\r\n                c = copy.copy(f1)\r\n                c.append(f2[k-2])\r\n                flag = True\r\n                for i in range(0,k-1):\r\n                    s = copy.copy(c)\r\n                    s.pop(i)\r\n                    if s not in F:\r\n                        flag = False\r\n                        break\r\n                if flag and c not in C:\r\n                    C.append(c)\r\n    return C\r\n\r\ndef compare_list(A,B):\r\n    if len(A) <= len(B):\r\n        for a in A:\r\n            if a not in B:\r\n                return False\r\n    else:\r\n        for b in B:\r\n            if b not in A:\r\n                return False\r\n    return True\r\n    \r\ndef apriori(T, minsup):\r\n    C = []\r\n    init = init_pass(T)\r\n    keys = init.keys()\r\n    keys.sort()\r\n    C.append(keys)\r\n    n = len(T)\r\n    F = [[]]\r\n    for f in C[0]:\r\n        if init[f]*1.0/n >= minsup:\r\n            F[0].append([f])\r\n    k = 1\r\n    while F[k-1] != []:\r\n        C.append(candidate_gen(F[k-1]))\r\n        F.append([])\r\n        for c in C[k]:\r\n            count = 0;\r\n            for t in T:\r\n                if compare_list(c,t):\r\n                    count += 1\r\n            if count*1.0/n >= minsup:\r\n                F[k].append(c)\r\n        k += 1\r\n    U = []\r\n    for f in F:\r\n        for x in f:\r\n            U.append(x)\r\n    return U\r\nT = [[\'A\',\'B\',\'C\',\'D\'],[\'B\',\'C\',\'E\'],[\'A\',\'B\',\'C\',\'E\'],[\'B\',\'D\',\'E\'],[\'A\',\'B\',\'C\',\'D\']]\r\nF = apriori(T, 0.9)\r\nprint F'),(35,34,19,'代码片段','\"author:TY\r\n\"site:http://pythoner.net\r\n\"email:admin@pythoner.net\r\n\"file:~/.vim\r\n\r\n\" 设定默认解码\r\nset fenc=utf-8\r\nset fencs=utf-8,usc-bom,euc-jp,gb18030,gbk,gb2312,cp936\r\n\r\n\" 侦测文件类型\r\nfiletype on\r\n\r\n\" 载入文件类型插件\r\nfiletype plugin on\r\n\r\n\" 为特定文件类型载入相关缩进文件\r\nfiletype indent on\r\n\r\n\" 带有如下符号的单词不要被换行分割\r\nset iskeyword+=_,$,@,%,#,-\r\n\r\n\" 语法高亮\r\nsyntax on\r\n\r\n\" 设置配色方案\r\ncolorscheme 256-jungle\r\n\r\n\" 字符间插入的像素行数目\r\nset linespace=0\r\n\r\n\" 在状态行上显示光标所在位置的行号和列号\r\nset ruler\r\nset rulerformat=%20(%2*%<%f%=\\ %m%r\\ %3l\\ %c\\ %p%%%)\r\n\r\n\" 在被分割的窗口间显示空白，便于阅读\r\nset fillchars=vert:\\ ,stl:\\ ,stlnc:\\\r\n\r\n\r\n\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\r\n\" 搜索和匹配\r\n\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\r\n\" 高亮显示匹配的括号\r\nset showmatch\r\n\r\n\" 在搜索的时候忽略大小写\r\nset ignorecase\r\n\r\n\" 在搜索时，输入的词句的逐字符高亮（类似firefox的搜索）\r\nset incsearch\r\n\r\n\" 我的状态行显示的内容（包括文件类型和解码）\r\nset statusline=%F%m%r%h%w\\[POS=%l,%v][%p%%]\\%{strftime(\\\"%d/%m/%y\\ -\\ %H:%M\\\")}\r\n\r\n\" 总是显示状态行\r\nset laststatus=1\r\n\r\n\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\r\n\" 文本格式和排版\r\n\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\r\n\r\n\" 继承前一行的缩进方式，特别适用于多行注释\r\nset autoindent\r\n\r\n\" 显示行号\r\nset nu\r\n\r\nset t_Co=256\r\n\r\n\" 用空格代替制表符\r\nset expandtab\r\n\r\n\" 制表符为4统一缩进为4\r\nset tabstop=4\r\nset softtabstop=4\r\nset shiftwidth=4\r\n\r\n\" 用空格键来开关折叠\r\nset foldenable\r\nset foldmethod=manual\r\nnnoremap <space> @=((foldclosed(line(\'.\')) < 0) ? \'zc\':\'zo\')<CR>\r\n\r\n\" 删除行尾部的空格\r\nmap <F2> :%s/\\s*$//g<cr>:noh<cr>\"\r\n\r\n\r\n\"pydiction 1.2(python语法高亮)\r\nfiletype plugin on\r\nlet g:pydiction_location = \'~/.vim/after/ftplugin/pydiction/complete-dict\'\r\n\"default g:pydiction_menu_height = 15\r\n\"let g:pydiction_menu_height =20\r\n\r\n\"settings for minibuffer\r\nlet g:miniBufExplMapWindowNavVim = 1\r\nlet g:miniBufExplMapWindowNavArrows = 1\r\nlet g:miniBufExplMapCTabSwitchBufs = 1\r\nlet g:miniBufExplModSelTarget = 1\r\n'),(36,35,19,'代码片段','#! -*- coding:utf-8 -*-\r\n \r\nimport string\r\nimport copy\r\ninputString=raw_input(\"请输入你要判断的字符串:\")\r\npunctuation=string.punctuation #sting.punctuation里面包含了32个英文标点符号\r\nidentify=\' \'*32\r\ntable=string.maketrans(punctuation,identify)#makerans接受两个等长的参数，形成一个对应表\r\nnew=inputString.translate(table).replace(\' \',\'\')#先用对应表和translate函数将字符串\r\n#里面的标点符号用空格代替，然后去掉空格\r\n \r\ninputString=list(new)#转换成列表，为的是使用反转函数\r\ntemp=copy.deepcopy(inputString)#深拷贝，这里我纠结了好久，如果只是用简单的等于号的话，\r\n#那样只是引用而已，inputstring改变了，temp也会改变的，所以要深拷贝\r\ninputString.reverse()#将列表反转\r\nprint inputString#打印出列表，为的是输出结果的时候，能观察上面的操作结果如何\r\nprint temp\r\nif temp==inputString:#判断是否相等\r\n    print \"是回文\"\r\nelse:\r\n    print \"不是回文\"'),(37,36,3,'代码片段','[piglei@~]$ sshhelper \r\n \r\nusage:\r\n \r\n    /usr/local/bin/sshhelper (hostname or short name)\r\n     \r\n    you can type a full hostname like 192.168.11.3 or a short name like \r\n    11.3 to ssh into it.\r\n     \r\n    - modify /home/piglei/.sshhelper_config to add your hosts.\r\n    - available hosts:\r\n \r\n        106.187.46.32        # CJ\'s linode\r\n        192.168.111.51       # 4 twisted (py_raincounter)\r\n \r\n[piglei@~]$ sshhelper 106\r\nStart login into 106.187.46.32, please wait...\r\n \r\nLast login: Mon Dec  5 05:51:35 2011 from 114.113.29.24\r\nroot@li389-32:~# '),(38,37,19,'代码片段','#!/usr/bin/env python2\r\n# -*- coding: utf-8 -*-\r\n\'\'\'\r\nCreated on 2011-11-25\r\nauthor: shiweifu\r\n  mail: shiweifu@126.com\r\n\'\'\'\r\nimport struct\r\nimport sys\r\nimport os\r\nimport pygame\r\nfrom random import randint\r\n\r\n\r\nimport pygame.sprite as Sprite\r\nfrom pygame.locals import *\r\n\r\ndebug = True\r\n\r\nFONT_NAME = \"Arial\"\r\nFONT_SIZE = 25\r\n\r\nSCREENRECT = Rect(0, 0, 640, 480)\r\nRECT_LEN = 15\r\n\r\nclass Box:\r\n    def __init__(self, x, y, f):\r\n        self.x = x\r\n        self.y = y\r\n        self.flag = f\r\n        if self.flag:\r\n            self.surface = get_font_surface(\"b\")\r\n        else:\r\n            self.surface = pygame.Surface((15,15))\r\n\r\n    def __str__(self):\r\n        return \"x:%s y:%s\" % (str(self.x),str(self.y))\r\n\r\n\r\nclass Link(object):\r\n    \r\n    def __init__(self, x, y, surface):\r\n        self.x = x\r\n        self.y = y\r\n        self.surface = surface\r\n    \r\n    def __str__(self):\r\n        return \"x:%s y:%s\" % (str(self.x),str(self.y))\r\n        \r\nclass Player:\r\n    def __init__(self,x,y):\r\n        self.snake_body = []\r\n        self.append(x,y)\r\n\r\n    def append(self,x,y):\r\n        l = Link(x,y,get_font_surface(\"s\",(255,0,0)))        \r\n        self.snake_body.append(l)\r\n\r\n    def abc(self):\r\n        print(\"test\")\r\n    \r\n    def move_left(self):\r\n        i = len(self.snake_body) - 1\r\n\r\n        while i >= 1:\r\n#            debug_output(\"i:\"+str(i))\r\n            self.snake_body[i].x = self.snake_body[i-1].x\r\n            self.snake_body[i].y = self.snake_body[i-1].y\r\n            i -= 1\r\n        self.snake_body[0].x -= 1        \r\n\r\n\r\n    def move_right(self):\r\n        i = len(self.snake_body) - 1\r\n\r\n        while i >= 1:\r\n#            debug_output(\"i:\"+str(i))\r\n            self.snake_body[i].x = self.snake_body[i-1].x\r\n            self.snake_body[i].y = self.snake_body[i-1].y\r\n            i -= 1\r\n        self.snake_body[0].x += 1\r\n\r\n    def move_up(self):\r\n        i = len(self.snake_body) - 1\r\n\r\n        while i >= 1:\r\n            self.snake_body[i].x = self.snake_body[i-1].x\r\n            self.snake_body[i].y = self.snake_body[i-1].y\r\n            i -= 1\r\n        self.snake_body[0].y -= 1\r\n    \r\n    def move_down(self):\r\n        i = len(self.snake_body) - 1\r\n\r\n        while i >= 1:\r\n            self.snake_body[i].x = self.snake_body[i-1].x\r\n            self.snake_body[i].y = self.snake_body[i-1].y\r\n            i -= 1\r\n        self.snake_body[0].y += 1\r\n\r\n    def move(self,target):\r\n        if target == \"up\":\r\n            self.move_up()\r\n        if target == \"down\":\r\n            self.move_down()\r\n        if target == \"left\":\r\n            self.move_left()\r\n        if target == \"right\":\r\n            self.move_right() \r\n                \r\ndef get_font_surface(s,color = (0,255,0),font_size=25):\r\n    font = pygame.font.SysFont(FONT_NAME,font_size)\r\n    #color = (0,255,0)\r\n    backcolor = (0,0,0)\r\n    r = font.render(s,True,color,backcolor)\r\n    return r\r\n\r\ndef create_map(width,height,rect_len):\r\n    y = 0\r\n    map_pos = []\r\n    snake_map = []\r\n    flag =True\r\n    while y < height:\r\n        x = 0        \r\n        flag = False\r\n        while x < width:        \r\n            if x < rect_len or x+rect_len >= width:\r\n                flag = True\r\n            elif y < rect_len or y+rect_len >= height:\r\n                flag = True\r\n            else:\r\n                flag = False\r\n\r\n            snake_map.append(Box(x/rect_len,y/rect_len,flag))\r\n            x += 15\r\n        y += 15\r\n\r\n    return snake_map\r\n\r\ndef debug_output(s, f=sys.stdout):\r\n    if debug == True:\r\n        f.write(\"debug info: \" + s + \"\\n\")\r\n        f.flush()\r\n\r\nclass Game:\r\n    \r\n    WIDTH = 640\r\n    HEIGHT = 480\r\n    BOX_SIZE = 15\r\n    \r\n    def __init__(self, caption=\"CooooolSnake\"):\r\n    \r\n        pygame.init()\r\n        pygame.display.set_caption(caption)\r\n        self.screen = pygame.display.set_mode(SCREENRECT.size, 0, 32)    \r\n        pygame.mouse.set_visible(0)\r\n        self.background = pygame.Surface(SCREENRECT.size)\r\n        \r\n        self.map_size = (Game.WIDTH/15-1,Game.HEIGHT/15-1)\r\n        \r\n        self.snake_map = create_map(Game.WIDTH,Game.HEIGHT,self.BOX_SIZE)\r\n        \r\n        self.stages = []\r\n        \r\n        self.__crate_player__()\r\n\r\n    def get_new_pos(self):\r\n        done = False\r\n        pos = None\r\n        while not done:\r\n            x = randint(2,self.map_size[0]-1)\r\n            y = randint(2,self.map_size[1]-2)\r\n\r\n            for link in self.player.snake_body:\r\n                if x == link.x and y == link.y:\r\n                    continue\r\n            pos = (x,y)\r\n            done = True\r\n\r\n        return pos\r\n               \r\n\r\n    def __crate_player__(self):\r\n        x = randint(2,self.map_size[0]-1)\r\n        y = randint(2,self.map_size[1]-2)\r\n        self.player = Player(x,y)\r\n\r\n    def is_over(self):\r\n        head = self.player.snake_body[0]\r\n        i = 0\r\n        \r\n        for link in self.player.snake_body[1:]:\r\n            if head.x == link.x and head.y == link.y:\r\n                return True \r\n        \r\n        for box in self.snake_map:\r\n            if box.flag:\r\n                if head.x == box.x and head.y == box.y:\r\n                    return True\r\n        return False\r\n        \r\n    def update_snake(self):\r\n        \r\n        for link in self.player.snake_body:\r\n            self.background.blit(link.surface,(link.x*Game.BOX_SIZE,link.y*Game.BOX_SIZE))\r\n            \r\n    def update_map(self):\r\n        for box in self.snake_map:\r\n            self.background.blit(box.surface,(box.x*Game.BOX_SIZE,box.y*Game.BOX_SIZE))\r\n\r\n    def add_stage(self):\r\n        x,y = self.get_new_pos()\r\n        s = get_font_surface(\"s\",(255,0,0))\r\n        l = Link(x,y,s)\r\n        self.stages.append(l)\r\n    \r\n    def update_stage(self):\r\n        head = self.player.snake_body[0]\r\n        for stage in self.stages:\r\n            if stage.x == head.x and stage.y == head.y:\r\n                self.player.append(stage.x,stage.y)\r\n                self.stages.remove(stage)\r\n                \r\n        for stage in self.stages:\r\n            self.background.blit(stage.surface,(stage.x*Game.BOX_SIZE,stage.y*Game.BOX_SIZE))\r\n\r\n    def refresh_background(self):\r\n        self.background.fill((0,0,0))\r\n        self.update_map()\r\n        self.update_snake()\r\n        \r\n        self.update_stage()\r\n        \r\n        self.screen.blit(self.background, (0, 0))\r\n        \r\n    def over(self):        \r\n        logo = get_font_surface(\"YOU ARE LOST!\",(0,0,255),60)\r\n        self.background.blit(logo,(100,200))\r\n\r\n        clock = pygame.time.Clock()\r\n        done = False\r\n\r\n        count = 0\r\n\r\n        while not done:\r\n            clock.tick(10)\r\n            count += 1\r\n            if count == 5:\r\n                done = True         \r\n            self.screen.blit(self.background, (0, 0))\r\n            pygame.display.flip()\r\n    \r\n    def loop(self):\r\n        pygame.display.update()\r\n        clock = pygame.time.Clock()\r\n        target = [\"up\",\"down\",\"left\",\"right\"][randint(0,3)]\r\n        \r\n        count = 0\r\n        stage_time = randint(0,50)\r\n\r\n        \r\n        done = False\r\n        while not done:\r\n            for e in pygame.event.get():\r\n                if e.type == pygame.KEYDOWN:\r\n                    keystate = pygame.key.get_pressed()\r\n                    if keystate[K_UP]:\r\n                        if target != \"down\":                        \r\n                            target = \"up\"\r\n\r\n                    elif keystate[K_DOWN]:\r\n                        if target != \"up\":                        \r\n                            target = \"down\"\r\n\r\n                    elif keystate[K_LEFT]:\r\n                        if target != \"right\":                        \r\n                            target = \"left\"\r\n\r\n                    elif keystate[K_RIGHT]:\r\n                        if target != \"left\":                        \r\n                            target = \"right\"\r\n                            \r\n                if e.type == QUIT:\r\n                    done = True\r\n            self.player.move(target)\r\n            if count == stage_time:\r\n                self.add_stage()\r\n                stage_time = randint(0,50)\r\n                count = 0\r\n \r\n            if self.is_over():\r\n                done = True\r\n                \r\n            clock.tick(10)\r\n            \r\n            count += 1\r\n            \r\n            self.refresh_background()\r\n            \r\n            if done:\r\n                self.over()\r\n            \r\n            pygame.display.flip()\r\n\r\nif __name__ == \'__main__\':\r\n    game = Game()\r\n    game.loop()\r\n    pass'),(39,38,19,'代码片段','#!/usr/bin/python\r\n# -*- coding: UTF-8 -*-\r\n\r\n# face_detect.py\r\n\r\n# Face Detection using OpenCV. Based on sample code from:\r\n# http://python.pastebin.com/m76db1d6b\r\n\r\n# Usage: python face_detect.py \r\n\r\nimport sys, os\r\nfrom opencv.cv import *\r\nfrom opencv.highgui import *\r\nfrom PIL import Image, ImageDraw\r\nfrom math import sqrt\r\n\r\ndef detectObjects(image):\r\n    \"\"\"Converts an image to grayscale and prints the locations of any faces found\"\"\"\r\n    grayscale = cvCreateImage(cvSize(image.width, image.height), 8, 1)\r\n    cvCvtColor(image, grayscale, CV_BGR2GRAY)\r\n\r\n    storage = cvCreateMemStorage(0)\r\n    cvClearMemStorage(storage)\r\n    cvEqualizeHist(grayscale, grayscale)\r\n\r\n    cascade = cvLoadHaarClassifierCascade(\r\n        \\\'/usr/share/opencv/haarcascades/haarcascade_frontalface_default.xml\\\',\r\n        cvSize(1,1))\r\n    faces = cvHaarDetectObjects(grayscale, cascade, storage, 1.1, 2,\r\n        CV_HAAR_DO_CANNY_PRUNING, cvSize(20,20))\r\n\r\n    result = []\r\n    for f in faces:\r\n        result.append((f.x, f.y, f.x+f.width, f.y+f.height))\r\n\r\n    return result\r\n\r\ndef grayscale(r, g, b):\r\n    return int(r * .3 + g * .59 + b * .11)\r\n\r\ndef process(infile, outfile):\r\n\r\n    image = cvLoadImage(infile);\r\n    if image:\r\n        faces = detectObjects(image)\r\n\r\n    im = Image.open(infile)\r\n\r\n    if faces:\r\n        draw = ImageDraw.Draw(im)\r\n        for f in faces:\r\n            draw.rectangle(f, outline=(255, 0, 255))\r\n\r\n        im.save(outfile, \"JPEG\", quality=100)\r\n    else:\r\n        print \"Error: cannot detect faces on %s\" % infile\r\n\r\nif __name__ == \"__main__\":\r\n    process(\\\'input.jpg\\\', \\\'output.jpg\\\')'),(40,39,19,'代码片段','import Queue, threading, sys  \r\nfrom threading import Thread  \r\nimport time,urllib  \r\n  \r\n# working thread  \r\nclass Worker(Thread):  \r\n    worker_count = 0  \r\n    def __init__( self, workQueue, resultQueue, timeout = 0, **kwds):  \r\n        Thread.__init__( self, **kwds )  \r\n        self.id = Worker.worker_count  \r\n        Worker.worker_count += 1  \r\n        self.setDaemon( True )  \r\n        self.workQueue = workQueue  \r\n        self.resultQueue = resultQueue  \r\n        self.timeout = timeout  \r\n  \r\n    def run( self ):  \r\n        \'\'\' the get-some-work, do-some-work main loop of worker threads \'\'\'  \r\n        while True:  \r\n            try:  \r\n                callable, args, kwds = self.workQueue.get(timeout=self.timeout)  \r\n                res = callable(*args, **kwds)  \r\n                print \"worker[%2d]: %s\" % (self.id, str(res) )  \r\n                self.resultQueue.put( res )  \r\n            except Queue.Empty:  \r\n                break  \r\n            except :  \r\n                print \'worker[%2d]\' % self.id, sys.exc_info()[:2]  \r\n                  \r\nclass WorkerManager:  \r\n    def __init__( self, num_of_workers=10, timeout = 1):  \r\n        self.workQueue = Queue.Queue()  \r\n        self.resultQueue = Queue.Queue()  \r\n        self.workers = []  \r\n        self.timeout = timeout  \r\n        self._recruitThreads( num_of_workers )  \r\n  \r\n    def _recruitThreads( self, num_of_workers ):  \r\n        for i in range( num_of_workers ):  \r\n            worker = Worker( self.workQueue, self.resultQueue, self.timeout )  \r\n            self.workers.append(worker)  \r\n  \r\n    def start(self):  \r\n        for w in self.workers:  \r\n            w.start()  \r\n  \r\n    def wait_for_complete( self):  \r\n        # ...then, wait for each of them to terminate:  \r\n        while len(self.workers):  \r\n            worker = self.workers.pop()  \r\n            worker.join( )  \r\n            if worker.isAlive() and not self.workQueue.empty():  \r\n                self.workers.append( worker )  \r\n        print \"All jobs are are completed.\"  \r\n  \r\n    def add_job( self, callable, *args, **kwds ):  \r\n        self.workQueue.put( (callable, args, kwds) )  \r\n  \r\n    def get_result( self, *args, **kwds ):  \r\n        return self.resultQueue.get( *args, **kwds )  '),(41,39,19,'代码片段','def test_job(id, sleep = 0.001 ):  \r\n    try:  \r\n        urllib.urlopen(\'https://www.gmail.com/\').read()  \r\n    except:  \r\n        print \'[%4d]\' % id, sys.exc_info()[:2]  \r\n    return  id  \r\n  \r\ndef test():  \r\n    import socket  \r\n    socket.setdefaulttimeout(10)  \r\n    print \'start testing\'  \r\n    wm = WorkerManager(10)  \r\n    for i in range(500):  \r\n        wm.add_job( test_job, i, i*0.001 )  \r\n    wm.start()  \r\n    wm.wait_for_complete()  '),(42,40,19,'browser.py','#encoding=utf-8\r\n\r\n# author:ty\r\n# site:pythoner.net\r\n# date:2012-01-00\r\n\r\nimport threading\r\nimport random\r\nimport time\r\nfrom Queue import Queue\r\nimport time,math,os,re,urllib,urllib2,cookielib\r\nfrom BeautifulSoup import BeautifulSoup\r\nimport time\r\nimport socket\r\nimport os\r\nfrom PIL import Image\r\nimport random\r\nimport db\r\n\r\nclass BrowserBase(object):\r\n    ERROR = {\r\n        \'0\':\'Can not open the url,checck you net\',\r\n        \'1\':\'Creat download dir error\',\r\n        \'2\':\'The image links is empty\',\r\n        \'3\':\'Download faild\',\r\n        \'4\':\'Build soup error,the html is empty\',\r\n        \'5\':\'Can not save the image to your disk\',\r\n    }\r\n    image_links = []\r\n    image_count = 0\r\n\r\n    def __init__(self):\r\n        socket.setdefaulttimeout(20)\r\n\r\n    def speak(self,name,content):\r\n        print \'[%s]%s\' %(name,content)\r\n\r\n    def openurl(self,url):\r\n        \"\"\"\r\n        打开网页\r\n        \"\"\"\r\n        cookie_support= urllib2.HTTPCookieProcessor(cookielib.CookieJar())\r\n        self.opener = urllib2.build_opener(cookie_support,urllib2.HTTPHandler)\r\n        urllib2.install_opener(self.opener)\r\n        user_agents = [\r\n                    \'Mozilla/5.0 (Windows; U; Windows NT 5.1; it; rv:1.8.1.11) Gecko/20071127 Firefox/2.0.0.11\',\r\n                    \'Opera/9.25 (Windows NT 5.1; U; en)\',\r\n                    \'Mozilla/4.0 (compatible; MSIE 6.0; Windows NT 5.1; SV1; .NET CLR 1.1.4322; .NET CLR 2.0.50727)\',\r\n                    \'Mozilla/5.0 (compatible; Konqueror/3.5; Linux) KHTML/3.5.5 (like Gecko) (Kubuntu)\',\r\n                    \'Mozilla/5.0 (X11; U; Linux i686; en-US; rv:1.8.0.12) Gecko/20070731 Ubuntu/dapper-security Firefox/1.5.0.12\',\r\n                    \'Lynx/2.8.5rel.1 libwww-FM/2.14 SSL-MM/1.4.1 GNUTLS/1.2.9\',\r\n                    \"Mozilla/5.0 (X11; Linux i686) AppleWebKit/535.7 (KHTML, like Gecko) Ubuntu/11.04 Chromium/16.0.912.77 Chrome/16.0.912.77 Safari/535.7\",\r\n                    \"Mozilla/5.0 (X11; Ubuntu; Linux i686; rv:10.0) Gecko/20100101 Firefox/10.0 \",\r\n\r\n                    ]\r\n\r\n        # 随机选取一个agent\r\n        agent = random.choice(user_agents)\r\n        self.opener.addheaders = [(\"User-agent\",agent),(\"Accept\",\"*/*\"),(\'Referer\',\'http://www.google.com\')]\r\n        try:\r\n            res = self.opener.open(url)\r\n        except Exception,e:\r\n            self.speak(str(e)+url)\r\n            raise Exception\r\n        else:\r\n            return res\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n    #main()\r\n    s = Spider()\r\n\r\n'),(43,41,19,'代码片段','#!/usr/bin/env python\r\n# encoding: utf-8\r\n \r\n\"\"\"\r\nMonitorLog.py\r\n \r\nUsage: MonitorLog.py ...\r\nMonitor the log file\r\n \r\n-f  log file\r\n-h  help info\r\n \r\npython MonitorLog.py -f C:\\monitor.log\r\n \r\nCreated by zhoubo on 2011-08-29.\r\n\"\"\"\r\n \r\nimport sys\r\nimport os\r\nimport getopt\r\nimport subprocess\r\nimport time\r\nimport codecs\r\nimport winsound\r\n \r\nABSPATH = os.path.dirname(os.path.abspath(__file__))\r\nMONITERCONF = \'moniter_keyword.txt\' #utf8 file\r\n \r\ndef main():\r\n    try:\r\n        opts, args = getopt.getopt(sys.argv[1:], \'hf:\')\r\n    except getopt.GetoptError, err:\r\n        print str(err)\r\n        print __doc__\r\n        return 1\r\n \r\n    path = \'\'\r\n    for k, v in opts:\r\n        if k == \'-f\':\r\n            path = v\r\n        elif k == \'-h\':\r\n            print __doc__\r\n            return 0\r\n \r\n    if not (path and os.path.exists(path)):\r\n        print \'Invalid path: %s\' % path \r\n        print __doc__\r\n        return 2\r\n \r\n    #命令行元组\r\n    cmd = (\'tail\', \'-f\', path)\r\n    print \' \'.join(cmd)\r\n    output = subprocess.Popen(cmd, stdout=subprocess.PIPE)\r\n \r\n    keywordMap = {}\r\n    #加载监控的关键字信息\r\n    with codecs.open(os.path.join(ABSPATH, MONITERCONF), \'r\', \'utf8\') as f:\r\n        lines = f.readlines()\r\n    for line in lines:\r\n        line = line.strip()\r\n        if not line:\r\n            continue\r\n        keyword, wav = line.strip().split(\':\')\r\n        keywordMap[keyword] = wav\r\n \r\n    while True:\r\n        line = output.stdout.readline()\r\n        #process code,得到输出信息后的处理代码\r\n        if not line:\r\n            time.sleep(0.01)\r\n            continue\r\n        line = line.strip().decode(\'utf8\')\r\n        print line\r\n        for keyword in keywordMap:\r\n            if line.find(keyword) > -1:\r\n                winsound.PlaySound(keywordMap[keyword], \r\n                                   winsound.SND_NODEFAULT)\r\n        #time.sleep(0.01)\r\n    return 0\r\n \r\nif __name__ == \'__main__\':\r\n    sys.exit(main())'),(44,42,19,'代码片段','class Festival(): \r\n    #国历节日 *表示放假日 \r\n    def solar_Fstv(solar_month, solar_day): \r\n        sFtv = [ \r\n        \"0101#元旦节#\", \r\n        \"0202#世界湿地日#\", \r\n        \"0210#国际气象节#\", \r\n        \"0214#情人节#\", \r\n        \"0301#国际海豹日#\", \r\n        \"0303#全国爱耳日#\", \r\n        \"0305#学雷锋纪念日#\", \r\n        \"0308#妇女节#\", \r\n        \"0312#植树节# #孙中山逝世纪念日#\", \r\n        \"0314#国际警察日#\", \r\n        \"0315#消费者权益日#\", \r\n        \"0317#中国国医节# #国际航海日#\", \r\n        \"0321#世界森林日# #消除种族歧视国际日# #世界儿歌日#\", \r\n        \"0322#世界水日#\", \r\n        \"0323#世界气象日#\", \r\n        \"0324#世界防治结核病日#\", \r\n        \"0325#全国中小学生安全教育日#\", \r\n        \"0330#巴勒斯坦国土日#\", \r\n        \"0401#愚人节# #全国爱国卫生运动月(四月)# #税收宣传月(四月)#\", \r\n        \"0407#世界卫生日#\", \r\n        \"0422#世界地球日#\", \r\n        \"0423#世界图书和版权日#\", \r\n        \"0424#亚非新闻工作者日#\", \r\n        \"0501#劳动节#\", \r\n        \"0504#青年节#\", \r\n        \"0505#碘缺乏病防治日#\", \r\n        \"0508#世界红十字日#\", \r\n        \"0512#国际护士节#\", \r\n        \"0515#国际家庭日#\", \r\n        \"0517#国际电信日#\", \r\n        \"0518#国际博物馆日#\", \r\n        \"0520#全国学生营养日#\", \r\n        \"0523#国际牛奶日#\", \r\n        \"0531#世界无烟日#\", \r\n        \"0601#国际儿童节#\", \r\n        \"0605#世界环境保护日#\", \r\n        \"0606#全国爱眼日#\", \r\n        \"0617#防治荒漠化和干旱日#\", \r\n        \"0623#国际奥林匹克日#\", \r\n        \"0625#全国土地日#\", \r\n        \"0626#国际禁毒日#\", \r\n        \"0701#中国共产党诞辰# #香港回归纪念日# #世界建筑日#\", \r\n        \"0702#国际体育记者日#\", \r\n        \"0707#抗日战争纪念日#\", \r\n        \"0711#世界人口日#\", \r\n        \"0730#非洲妇女日#\", \r\n        \"0801#建军节#\", \r\n        \"0808#中国男子节(爸爸节)#\", \r\n        \"0815#抗日战争胜利纪念#\", \r\n        \"090