INSERT INTO `topic_tag` VALUES (1,'django','python中很有前途的开源web框架,它源自一个在线新闻 Web 站点，于 2005 年以开源的形式被释放出来。'),(3,'web.py','一个小巧灵活的Python框架，它简单而且功能强大。'),(4,'node.js',NULL),(5,'web2py','一个为Python语言提供的全功能Web应用框架，只在敏捷快速的开发Web应用，具有快速、安全以及可移植的数据库驱动的应用，兼容 Google App Engine'),(6,'mod_wsgi',NULL),(7,'mod_python','mod_python是apache组织的一个项目，通过它，可以开发psp或cgi，mod_python功能强大，速度快，是非常优秀的web开发工具。 '),(8,'pycharm','由JetBrains打造的一款Python IDE。我们知道，VS2010的重构插件Resharper就是出自JetBrains之手。'),(9,'wingide','相当优秀的 IDE；其编辑器包括大量语言的语法标签高亮显示，虽然它只是个面向 Python 的工具。源代码浏览器对浏览项目或模块非常实用'),(10,'pydev','Eclipse下用来开发Python的插件'),(11,'minidom',NULL),(12,'socket',NULL),(13,'urllib',NULL),(14,'wxpython','Python语言的一套优秀的GUI图形库，允许Python程序员很方便的创建完整的、功能键全的GUI用户界面'),(15,'py2exe','一个用来将 Python 程序转化成 exe 可执行程序的小工具'),(16,'pygame','一组用来开发游戏软件的 Python 程序模块，基于 SDL 库的基础上开发。允许你在 Python 程序中创建功能丰富的游戏和多媒体程序'),(17,'pyside',NULL),(18,'pygui',NULL),(19,'zope','Zope是一个开源的web应用服务器，主要用python写成。它是一个事务型的对象数据库平台,Zope除了能储存内容，数据外，还能存放动态的 HTML模板、脚本、搜索引擎、关系数据库管理系统 '),(20,'tornado','Tornado web server 是使用Python编写出來的一个极轻量级、高可伸缩性和非阻塞IO的Web服务器软件，著名的 Friendfeed 网站就是使用它搭建'),(21,'unicode',NULL),(22,'uwsgi',''),(23,'vps',''),(24,'linux',''),(25,'unix',''),(26,'Apache','Apache HTTP Server（简称Apache）是Apache软件基金会的一个开放源码的网页服务器，可以在大多数计算机操作系统中运行，由于其多平台和安全性被广泛使用，是最流行的Web服务器之一'),(27,'pyuml','一个 Eclipse 的插件，用来根据UML模型生成 Python 代码，基于 PyDev 和 Eclipse Uml2Tools 开发。'),(28,'pylint','一个 Python 代码分析工具，它分析 Python 代码中的错误，查找不符合代码风格标准（Pylint 默认使用的代码风格是 PEP 8）和有潜在问题的代码。'),(29,'PyBB','使用 Python 语言开发的论坛软件，基于 django 框架。'),(30,'OSQA','一款免费且开源的问答系统，采用Python的Django开发框架，基于中国优秀的问答系统CNProg，非常类似国外著名的技术问答网站http://stackoverflow.com。'),(31,'PyS60','Symbian S60 手机上的 Python 语言开发环境。'),(32,'Jython','Python的纯Java实现。她无缝地结合了Java类与Python，使用户能以Python语言的语法编写在Java虚拟机上运行的 软件。'),(33,'CPython','C语言实现的Python解释器，也是官方的并且是最广泛使用的Python解释器。除了CPython以外，还有用JAVA实现的Jython和用.NET实现的IronPython'),(34,'LBForum','用django开发的论坛系统，界面部模仿FluxBB'),(35,'招聘',''),(36,'stackless','');
